INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 1} \\frac{{(2x - 3)(\\sqrt x  - 1)}}{{2{x^2} + x -3}} = \\)','(a)-1/10','(b)1/10','(c)-1/8','(d)None of these ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to 1} \\,\\,\\frac{{(2x - 3)\\,(\\sqrt x  - 1) \\times(\\sqrt x  + 1)}}{{(x - 1)\\,(2x + 3) \\times (\\sqrt x  + 1)}} = \\frac{{ -1}}{{5\\,.\\,2}} = \\frac{{ - 1}}{{10}}.\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(\\mathop {\\lim }\\limits_{x \\to 0} kx\\,{\\rm{cosec}}\\,x = \\mathop {\\lim}\\limits_{x \\to 0} x\\,{\\rm{cosec}}\\;kx\\), then \\(k = \\)','(a)1','(b)-1','(c)\\( \\pm 1\\)','(d)\\( \\pm \\,2\\) ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,kx\\cos ec\\,x = \\mathop {\\lim }\\limits_{x\\to 0} x\\cos ec\\,\\,kx\\)    \\( \\Rightarrow \\,\\,k\\,\\,.\\,\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{x}{{\\sinx}} = \\frac{1}{k}\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{kx}}{{\\sin \\,kx}}\\)\\(\\Rightarrow \\,\\,k = \\frac{1}{k}\\,\\, \\Rightarrow \\,\\,k =  \\pm \\,1.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{e^{1/x}} - 1}}{{{e^{1/x}} + 1}} =\\)','(a)0','(b)1','(c)-1','(d)Does not exist    ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(f(x) = \\left( {\\frac{{{e^{1/x}} - 1}}{{{e^{1/x}} + 1}}} \\right)\\,,\\) then\\(\\mathop {\\lim }\\limits_{x \\to \\,0 + } \\,f(x) = \\mathop {\\lim }\\limits_{h \\to\\,0} \\left( {\\frac{{{e^{1/h}} - 1}}{{{e^{1/h}} + 1}}} \\right) = \\mathop {\\lim}\\limits_{h \\to \\,0} \\frac{{{e^{1/h}}\\left( {1 - \\frac{1}{{{e^{1/h}}}}}\\right)}}{{{e^{1/h}}\\left( {1 + \\frac{1}{{{e^{1/h}}}}} \\right)}} = 1\\)Similarly \\(\\mathop {\\lim }\\limits_{x \\to \\,0 - } f(x) =  - 1\\). Hence limitdoes not exist. ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\log \\cos x}}{x} =\\)','(a)0','(b)1','(c)\\(\\infty \\)','(d)None of these   ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{\\log \\cos x}}{x} = \\mathop{\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{\\log \\,\\left[ {1 - 2{{\\sin }^2}\\frac{x}{2}}\\right]}}{x}\\)\\(= \\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{ - \\,\\left[ {2\\,{{\\sin}^2}\\frac{x}{2} + {{\\left( {\\frac{{2\\,{{\\sin }^2}\\frac{x}{2}}}{2}} \\right)}^2} +......} \\right]}}{x} = 0\\)Aliter : Apply L-Hospitals rule,\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{\\log \\cos x}}{x} = \\mathop {\\lim}\\limits_{x \\to 0} \\,\\frac{{ - \\tan x}}{1} = 0.\\)    ',CURRENT_TIMESTAMP);
