INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(9) = 9\\), \\(f(9) = 4\\), then \\(\\mathop {\\lim }\\limits_{x \\to 9}\\frac{{\\sqrt {f(x)}  - 3}}{{\\sqrt x  - 3}} = \\)','(a)2','(b)4','(c)-2','(d)-4   ','2','FUNCTION, L, C, D','2','Answer =  (b)Applying L-Hospitals rule,\\(\\mathop {\\lim }\\limits_{x \\to 9} \\frac{{\\frac{1}{{2\\sqrt {f(x)} }} \\cdotf(x)}}{{\\frac{1}{{2\\sqrt x }}}} = \\frac{{\\frac{{f(9)}}{{\\sqrt {f(9)}}}}}{{\\frac{1}{{\\sqrt 9 }}}} = \\frac{{\\frac{4}{3}}}{{\\frac{1}{3}}} = 4\\)    ',CURRENT_TIMESTAMP);
