INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{h \\to 0} \\frac{{\\sqrt {x + h}  - \\sqrt x }}{h} =\\)','(a)\\(\\frac{1}{{2\\sqrt x }}\\)','(b)\\(\\frac{1}{{\\sqrt x }}\\)','(c)\\(2\\sqrt x \\)','(d)\\(\\sqrt x \\)  ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{h \\to 0} \\,\\,\\frac{{\\sqrt {x + h}  - \\sqrt x }}{h}= \\mathop {\\lim }\\limits_{h \\to 0} \\,\\,\\frac{{{{(\\sqrt {x + h} )}^2} - {{(\\sqrt x)}^2}}}{{h\\,(\\sqrt {x + h}  + \\sqrt x )}} = \\frac{1}{{2\\sqrt x }}\\).Aliter : Apply L-Hospital rule,   \\(\\mathop {\\lim }\\limits_{h \\to 0} \\,\\,\\frac{{\\sqrt {x + h}  - \\sqrt x }}{h} =\\mathop {\\lim }\\limits_{h \\to 0} \\,\\,\\frac{1}{{2\\sqrt {x + h} }} =\\frac{1}{{2\\sqrt x }}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{2^x} - 1}}{{{{(1 + x)}^{1/2}} - 1}}= \\)','(a)\\(\\log 2\\)','(b)\\(\\log 4\\)','(c)\\(\\log \\sqrt 2 \\)','(d)None of these  ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{{2^x} - 1}}{{{{(1 + x)}^{1/2}} -1}} = \\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{{2^x}\\log 2}}{{{\\textstyle{1\\over 2}}\\,{{(1 + x)}^{ - 1/2}}}}\\)\\(\\left\\{ {\\,\\,\\,\\mathop {\\lim }\\limits_{x \\to a} \\,\\frac{{f(x)}}{{g(x)}} =\\mathop {\\lim }\\limits_{x \\to a} \\,\\frac{{f(x)}}{{g(x)}}} \\right\\}\\)\\(= 2\\,\\log 2 = \\log 4.\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{1 - \\cos mx}}{{1 - \\cos nx}} = \\)','(a)\\(m/n\\)','(b)\\(n/m\\)','(c)\\(\\frac{{{m^2}}}{{{n^2}}}\\)','(d)\\(\\frac{{{n^2}}}{{{m^2}}}\\) ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{1 - \\cos mx}}{{1 - \\cos \\,nx}} =\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\left\\{ {\\frac{{2\\,{{\\sin}^2}{\\textstyle{{mx} \\over 2}}}}{{2\\,{{\\sin }^2}{\\textstyle{{nx} \\over 2}}}}}\\right\\}\\)\\(= \\mathop {\\lim }\\limits_{x \\to 0} \\,\\left[ {{{\\left\\{ {\\frac{{\\sin{\\textstyle{{mx} \\over 2}}}}{{{\\textstyle{{mx} \\over 2}}}}}\\right\\}}^2}\\,\\,\\,\\frac{{{m^2}{x^2}}}{4}.\\frac{1}{{{{\\left\\{ {\\frac{{\\sin{\\textstyle{{nx} \\over 2}}}}{{{\\textstyle{{nx} \\over 2}}}}}\\right\\}}^2}}}.\\frac{4}{{{n^2}{x^2}}}} \\right]\\)\\(= \\frac{{{m^2}}}{{{n^2}}} \\times 1 = \\frac{{{m^2}}}{{{n^2}}}.\\)Aliter : Apply L-Hospitals rule,\\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{1 - \\cos mx}}{{1 - \\cos nx}} = \\mathop{\\lim }\\limits_{x \\to 0} \\frac{{m\\sin mx}}{{n\\sin nx}} = \\mathop {\\lim}\\limits_{x \\to 0} \\frac{{{m^2}\\cos mx}}{{{n^2}\\cos nx}} =\\frac{{{m^2}}}{{{n^2}}}.\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{e^{\\sin x}} - 1}}{x} =\\)','(a)1','(b)e','(c)1/e','(d)None of these  ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{{e^{\\sin x}} - 1}}{x} = \\mathop{\\lim }\\limits_{x \\to 0} \\,\\frac{{{e^{\\sin x}} - 1}}{{\\sin x}} \\times \\frac{{\\sinx}}{x}\\)Aliter :\\(= \\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{{e^{\\sin x}} - 1}}{{\\sin x}} \\times\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{\\sin x}}{x} = 1 \\times 1 = 1.\\)Apply L-Hospitals rule,\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{{e^{\\sin x}} - 1}}{x} = \\mathop {\\lim}\\limits_{x \\to 0} \\,\\frac{{\\cos x\\,{e^{\\sin x}}}}{1} = 1.\\,{e^0} = 1.\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\sqrt x (\\sqrt {x + 5}  - \\sqrt x ) =\\)','(a)5','(b)3','(c)5/2','(d)3/2 ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\,\\sqrt x \\,(\\sqrt {x + 5}  - \\sqrtx ) \\times \\frac{{(\\sqrt {x + 5}  + \\sqrt x )}}{{(\\sqrt {x + 5}  + \\sqrt x )}}\\)  \\(= \\mathop {\\lim }\\limits_{x \\to \\infty } \\frac{{\\sqrt x \\,(5)}}{{\\sqrt x\\,\\left( {\\sqrt {1 + \\frac{5}{x}}  + 1} \\right)}} = \\frac{5}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 1} \\frac{{x - 1}}{{2{x^2} - 7x + 5}} = \\) ','(a)1/3','(b)1/11','(c)-1/3','(d)None of these  ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to 1} \\,\\,\\frac{{x - 1}}{{(x - 1)\\,(2x - 5)}} =  -\\frac{1}{3}\\).Aliter : Apply L-Hospitals rule.    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\frac{{\\sin x}}{x} = \\) ','(a)1','(b)0','(c)Does not exist ','(d)None of these   ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\,\\,\\frac{{\\sin x}}{x},\\) let \\(x =\\frac{1}{y}\\) or \\(y = \\frac{1}{x},\\) so that \\(x \\to \\infty \\,\\, \\Rightarrow \\,y\\to 0\\)\\(\\therefore \\,\\mathop {\\lim }\\limits_{x \\to \\infty } \\,\\left( {\\frac{{\\sinx}}{x}} \\right) = \\mathop {\\lim }\\limits_{y \\to 0} \\,\\left( {y.\\sin \\frac{1}{y}}\\right) = \\mathop {\\lim }\\limits_{y \\to 0} \\,y \\times \\mathop {\\lim }\\limits_{y\\to 0} \\,\\sin \\frac{1}{y} = 0 \\times ... = 0\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\sqrt {1 + \\sin x}  - \\sqrt {1 - \\sin x}}}{x} =\\)','(a)-1','(b)1','(c)2','(d)-2   ','2','FUNCTION, L, C, D','2','Answer =  (b)Apply L-Hospitals rule, \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\sqrt {1 +\\sin x}  - \\sqrt {1 - \\sin x} }}{x}\\)\\(= \\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{\\cos x}}{{2\\sqrt {1 + \\sin x} }}+ \\frac{{\\cos x}}{{2\\sqrt {1 - \\sin x} }} = \\frac{1}{2} + \\frac{1}{2} = 1.\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{2{{\\sin }^2}3x}}{{{x^2}}} = \\)','(a)6','(b)9','(c)18','(d)3  ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{2 \\times 9\\,{{\\sin}^2}3x}}{{{{(3x)}^2}}} = 18\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{\\alpha  \\to \\pi /4} \\frac{{\\sin \\alpha  - \\cos \\alpha}}{{\\alpha  - \\frac{\\pi }{4}}} = \\) ','(a)\\(\\sqrt 2 \\)','(b)\\(1/\\sqrt 2 \\)','(c)1','(d)None of these  ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{\\alpha  \\to \\pi /4} \\,\\frac{{\\sin \\alpha  - \\cos\\alpha }}{{\\alpha  - \\pi /4}}\\)\\(= \\mathop {\\lim }\\limits_{\\alpha  \\to \\pi /4} \\,\\left\\{ {\\frac{{\\sqrt 2 \\left({\\sin \\alpha .\\frac{1}{{\\sqrt 2 }} - \\cos \\alpha .\\frac{1}{{\\sqrt 2 }}}\\right)}}{{\\left( {\\alpha  - \\frac{\\pi }{4}} \\right)}}} \\right\\}\\)\\(= \\sqrt 2 \\,\\mathop {\\lim }\\limits_{\\alpha  \\to \\pi /4} \\,\\frac{{\\sin \\,\\left({\\alpha  - \\frac{\\pi }{4}} \\right)}}{{\\left( {\\alpha  - \\frac{\\pi }{4}} \\right)}}= \\sqrt 2  \\times 1 = \\sqrt 2 .\\)Aliter : Apply L-Hospitals rule,   \\(\\mathop {\\lim }\\limits_{\\alpha  \\to \\pi /4} \\,\\frac{{\\sin \\alpha  - \\cos \\alpha}}{{\\alpha  - (\\pi /4)}} = \\mathop {\\lim }\\limits_{\\alpha  \\to \\pi /4}\\,\\frac{{\\cos \\alpha  + \\sin \\alpha }}{1} = \\frac{1}{{\\sqrt 2 }} +\\frac{1}{{\\sqrt 2 }} = \\sqrt 2 .\\)',CURRENT_TIMESTAMP);
