INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(\\mathop {\\lim }\\limits_{x \\to a} \\frac{{{x^9} + {a^9}}}{{x + a}} = 9\\),then \\(a = \\)','(a)\\({9^{1/8}}\\)','(b)\\( \\pm 2\\)','(c)\\( \\pm 3\\)','(d)None of these   ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to a} \\,\\frac{{{x^9} + {a^9}}}{{x + a}} = 9\\,\\,\\Rightarrow \\,\\,\\frac{{2{a^9}}}{{2a}} = 9\\,\\, \\Rightarrow \\,\\,{a^8} = 9\\)\\(\\Rightarrow \\,\\,\\,\\,a = {9^{1/8}}\\)    ',CURRENT_TIMESTAMP);
