INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\sin 2x + \\sin 6x}}{{\\sin 5x - \\sin3x}} = \\)','(a)1/2','(b)1/4','(c)2','(d)4  ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{2\\sin 4x\\cos 2x}}{{2\\sin x\\cos4x}} = \\mathop {\\lim }\\limits_{x \\to 0} 4\\left( {\\frac{{\\sin 4x}}{{4x}}}\\right)\\,\\left( {\\frac{x}{{\\sin x}}} \\right)\\frac{{\\cos 2x}}{{\\cos 4x}} = 4\\).Aliter : \\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{\\frac{{2\\,\\,\\sin2x}}{{2x}} + \\frac{{6\\,\\,\\sin 6x}}{{6x}}}}{{\\frac{{5\\,\\,\\sin 5x}}{{5x}} -\\frac{{3\\,\\,\\sin 3x}}{{3x}}}} = \\frac{{2 + 6}}{{5 - 3}} = 4.\\) ',CURRENT_TIMESTAMP);
