INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\frac{{\\sqrt {{x^2} + {a^2}}  - \\sqrt{{x^2} + {b^2}} }}{{\\sqrt {{x^2} + {c^2}}  - \\sqrt {{x^2} + {d^2}} }} =\\)','(a)\\(\\frac{{{a^2} - {b^2}}}{{{c^2} - {d^2}}}\\)','(b)\\(\\frac{{{a^2} + {b^2}}}{{{c^2}- {d^2}}}\\)','(c)\\(\\frac{{{a^2} + {b^2}}}{{{c^2} + {d^2}}}\\)','(d)None of these  ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\,\\frac{{({a^2} - {b^2})}}{{({c^2} -{d^2})}}\\,\\frac{{\\left[ {\\sqrt {1 + \\frac{{{c^2}}}{{{x^2}}}}  + \\sqrt {1 +\\frac{{{d^2}}}{{{x^2}}}} } \\right]}}{{\\left[ {\\sqrt {1 + \\frac{{{a^2}}}{{{x^2}}}} + \\sqrt {1 + \\frac{{{b^2}}}{{{x^2}}}} } \\right]}} = \\frac{{{a^2} -{b^2}}}{{{c^2} - {d^2}}}.\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to \\pi /2} \\frac{{2x - \\pi }}{{\\cos x}} = \\) ','(a)2','(b)1','(c)-2','(d)None of these  ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to \\pi /2} 2\\,\\left[ {\\frac{{x - \\frac{\\pi}{2}}}{{\\sin \\,\\left( {\\frac{\\pi }{2} - x} \\right)}}} \\right] =  - \\,2\\)Aliter : Apply L-Hospitals rule. ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\sin x - x}}{{{x^3}}} = \\) [MNR, 86]','(a)\\(\\frac{1}{3}\\)','(b)\\( - \\frac{1}{3}\\)','(c)\\(\\frac{1}{6}\\)','(d)\\( - \\frac{1}{6}\\)   ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{\\sin x - x}}{{{x^3}}}\\)Expand sin x, then\\(= \\mathop {\\lim }\\limits_{x \\to 0} \\frac{{ - \\frac{{{x^3}}}{{3\\,!}} +\\frac{{{x^5}}}{{5\\,!}} - ...}}{{{x^3}}} = \\mathop {\\lim }\\limits_{x \\to 0} \\left[{ - \\frac{1}{{3\\,!}} + \\frac{{{x^2}}}{{5\\,!}} - ...} \\right] = \\frac{{ -1}}{{3\\,!}} = \\frac{{ - 1}}{6}.\\)Aliter : Apply L-Hospitals rule. ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{h \\to 0} \\frac{{{{(a + h)}^2}\\sin (a + h) - {a^2}\\sina}}{h} = \\)','(a)\\(a\\cos a + {a^2}\\sin a\\)','(b)\\(a\\sin a + {a^2}\\cos a\\)','(c)\\(2a\\sin a + {a^2}\\cos a\\)','(d)\\(2a\\cos a + {a^2}\\sin a\\)  ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\frac{d}{{da}}\\,[{a^2}\\sin a] = 2a\\sin a + {a^2}\\cos a.\\)Aliter : Apply L-Hospitals rule,\\(\\mathop {\\lim }\\limits_{h \\to 0} \\,\\frac{{{{(a + h)}^2}\\sin (a + h) - {a^2}\\sina}}{h}\\)\\(= \\mathop {\\lim }\\limits_{h \\to 0} \\,\\,\\frac{{2\\,(a + h)\\,\\sin \\,(a + h) + {{(a+ h)}^2}\\cos \\,(a + h)}}{1}\\)\\(= 2a\\,\\,\\sin a + {a^2}\\cos \\,\\,a.\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 3} \\left\\{ {\\frac{{x - 3}}{{\\sqrt {x - 2}  -\\sqrt {4 - x} }}} \\right\\} = \\)','(a)1','(b)2','(c)-1','(d)-2 ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to 3} \\,\\left\\{ {\\frac{{x - 3}}{{\\sqrt {x - 2} - \\sqrt {4 - x} }}} \\right\\} = \\mathop {\\lim }\\limits_{x \\to 3} \\,\\frac{{(x -3)\\,\\left\\{ {\\sqrt {x - 2}  + \\sqrt {4 - x} } \\right\\}}}{{2\\,(x - 3)}} = 1\\).Aliter : Apply L-Hospitals rule.  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{x\\cos x - \\sin x}}{{{x^2}\\sin x}} = \\)[MNR ,~86]','(a)\\(\\frac{1}{3}\\)','(b)\\( - \\frac{1}{3}\\)','(c)1','(d)None of these  ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{x\\cos x - \\sin x}}{{{x^2}\\sinx}}\\)\\( = \\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{ - \\sin x}}{{2\\sin x +x\\cos x}}\\)(By L-Hospitals rule)\\( = \\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{ - \\cos x}}{{3\\cos x - x\\sinx}} =  - \\frac{1}{3}\\), (Again by L-Hospitals rule)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\frac{{(x - 1)(2x + 3)}}{{{x^2}}} =\\)','(a)1','(b)-1','(c)2','(d)None of these    ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\,\\,\\frac{{(x - 1)\\,\\,(2x +3)}}{{{x^2}}} = \\mathop {\\lim }\\limits_{x \\to \\infty } \\,\\,\\frac{{2{x^2} + x -3}}{{{x^2}}} = 2.\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\left[ {\\frac{{{1^3} + {2^3} + {3^3} +....... + {n^3}}}{{{n^4}}}} \\right] =\\)','(a)\\(\\frac{1}{2}\\)','(b)\\(\\frac{1}{3}\\)','(c)\\(\\frac{1}{4}\\)','(d)None of these    ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{n \\to \\infty } \\frac{{{{\\left( {1 + \\frac{1}{n}}\\right)}^2}}}{4} = \\frac{1}{4}.\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{y^2}}}{x} = ........\\), where \\({y^2}= ax + b{x^2} + c{x^3}\\)','(a)0','(b)1','(c)a','(d)None of these  ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{ax + b{x^2} + c{x^3}}}{x} =\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{a + bx + c{x^2}}}{1} = a.\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{{(1 + x)}^{1/2}} - {{(1 -x)}^{1/2}}}}{x} = \\) ','(a)0','(b)1/2','(c)1','(d)-1    ','3','FUNCTION, L, C, D','2','Answer =  (c)Multiply function by \\(\\frac{{{{(1 + x)}^{1/2}} + {{(1 - x)}^{1/2}}}}{{{{(1 +x)}^{1/2}} + {{(1 - x)}^{1/2}}}}\\) and solve.Aliter : Apply L-Hospitals rule,   \\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{{{(1 + x)}^{1/2}} - {{(1 -x)}^{1/2}}}}{x} = \\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{1}{{2\\sqrt {1 + x} }}+ \\frac{1}{{2\\sqrt {1 - x} }} = 1.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 1} \\frac{{{x^3} - 1}}{{{x^2} + 5x - 6}} =\\)','(a)0','(b)\\(\\frac{3}{7}\\)','(c)\\(\\frac{1}{2}\\)','(d)\\( - \\frac{1}{6}\\)   ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to 1} \\,\\frac{{(x - 1)\\,\\,({x^2} + x + 1)}}{{(x -1)\\,\\,(x + 6)}} = \\frac{3}{7}\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to a} \\frac{{\\sqrt {a + 2x}  - \\sqrt {3x} }}{{\\sqrt{3a + x}  - 2\\sqrt x }} = \\) ','(a)\\(\\frac{1}{{\\sqrt 3 }}\\)','(b)\\(\\frac{2}{{3\\sqrt 3 }}\\)','(c)\\(\\frac{2}{{\\sqrt 3 }}\\)','(d)\\(\\frac{2}{3}\\)    ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to a} \\,\\frac{{\\sqrt {a + 2x}  - \\sqrt {3x}}}{{\\sqrt {3a + x}  - 2\\sqrt x }}\\)\\(= \\mathop {\\lim }\\limits_{x \\to a} \\,\\frac{{\\sqrt {a + 2x}  - \\sqrt {3x}}}{{\\sqrt {3a + x}  - 2\\sqrt x }} \\times \\frac{{\\sqrt {a + 2x}  + \\sqrt {3x}}}{{\\sqrt {a + 2x}  + \\sqrt {3x} }} \\times \\frac{{\\sqrt {3a + x}  + 2\\sqrt x}}{{\\sqrt {3a + x}  + 2\\sqrt x }}\\)\\(= \\mathop {\\lim }\\limits_{x \\to a} \\frac{{\\sqrt {3a + x}  + 2\\sqrt x}}{{3\\,(\\sqrt {a + 2x}  + \\sqrt {3x)} }} = \\frac{2}{{3\\sqrt 3 }}.\\)Aliter : Apply L-Hospitals rule. ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 1} \\frac{{1 - {x^{ - 1/3}}}}{{1 - {x^{ - 2/3}}}}= \\) ','(a)\\(\\frac{1}{3}\\)','(b)\\(\\frac{1}{2}\\)','(c)\\(\\frac{2}{3}\\)','(d)\\( - \\frac{2}{3}\\)    ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to 1} \\,\\frac{{1 - {x^{ - 1/3}}}}{{(1 - {x^{ -1/3}})\\,\\,(1 + {x^{ - 1/3}})}} = \\frac{1}{2}.\\)Aliter : Apply L-Hospitals rule.   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{{(1 + x)}^n} - 1}}{x} = \\)','(a)n','(b)1','(c)-1','(d)None of these  ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{(1 + nx + {\\,^n}{C_2}{x^2} +...{\\rm{higher powers of }}x{\\rm{ to }}{x^n}) - 1}}{x} = n\\).Aliter : Apply L-Hospitals rule.    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\left( {\\frac{{\\tan 3x}}{x} + \\cos x} \\right) =\\)','(a)3','(b)1','(c)4','(d)2    ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,3\\,\\frac{{\\tan 3x}}{{3x}} + \\mathop{\\lim }\\limits_{x \\to 0} \\cos x = 3 + 1 = 4\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\sqrt {1 + x}  - \\sqrt {1 - x}}}{{{{\\sin }^{ - 1}}x}} = \\) ','(a)2','(b)1','(c)-1','(d)None of these   ','2','FUNCTION, L, C, D','2','Answer =  (b)Let \\({\\sin ^{ - 1}}x = y\\,\\, \\Rightarrow x = \\sin y\\)So \\(\\mathop {\\lim }\\limits_{y \\to 0} \\frac{{\\sqrt {1 + \\sin y}  - \\sqrt {1 -\\sin y} }}{y}\\)  \\((\\,\\,\\,x \\to 0 \\Rightarrow y \\to 0)\\)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\log (3 + x) - \\log (3 - x)}}{x} = k\\)=1Aliter : Apply L-Hospitals rule.    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{y \\to 0} \\frac{{(x + y)\\sec (x + y) - x\\sec x}}{y} =\\)','(a)\\(\\sec x(x\\tan x + 1)\\)','(b)\\(x\\tan x + \\sec x\\)','(c)\\(x\\sec x + \\tan x\\)','(d)None of these  ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{y \\to 0} \\,\\left\\{ {\\frac{{x\\,\\left\\{ {\\sec \\,(x +y) - \\sec x} \\right\\}}}{y} + \\sec \\,(x + y)} \\right\\}\\)\\(= \\mathop {\\lim }\\limits_{y \\to 0} \\,\\left[ {\\frac{x}{y}\\,\\left\\{ {\\frac{{\\cosx - \\cos \\,(x + y)}}{{\\cos \\,(x + y)\\,\\cos x}}} \\right\\}} \\right] + \\mathop {\\lim}\\limits_{y \\to 0} \\sec \\,(x + y)\\)\\(= \\mathop {\\lim }\\limits_{y \\to 0} \\,\\left[ {\\frac{{x\\sin \\,\\left( {x +\\frac{y}{2}} \\right)}}{{\\cos \\,(x + y)\\,.\\,\\,\\cos x}}\\,.\\,\\frac{{\\sin \\,\\left({\\frac{y}{2}} \\right)}}{{\\,\\,\\,\\frac{y}{2}}}} \\right] + \\sec x\\)= xtanxsecx + secx = secx(xtanx+1).Aliter : Apply L-Hospitals rule,\\(\\mathop {\\lim }\\limits_{y \\to 0} \\,\\frac{{(x + y)\\,\\sec \\,(x + y) - x\\,\\secx}}{y}\\)\\(= \\mathop {\\lim }\\limits_{y \\to 0} \\,\\frac{{(x + y)\\,\\sec \\,(x + y)\\tan \\,(x +y) + \\sec \\,(x + y) - 0}}{1}\\){Differentiating w.r.t.y assuming x as constant}\\(= x\\sec x\\tan x + \\sec x. = \\sec x(x\\tan x + 1)\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{x{{.2}^x} - x}}{{1 - \\cos x}} = \\)','(a)0','(b)\\(\\log 4\\)','(c)\\(\\log 2\\)','(d)None of these   ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{x.({2^x} - 1)}}{{1 - \\cos x}} =\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{2^x} - 1}}{x}.\\frac{{{x^2}}}{{1 - \\cosx}}\\) \\(= \\log \\,\\,2\\,.\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{{x^2}}}{{2\\,\\,{{\\sin}^2}\\frac{x}{2}}} = (\\log \\,\\,2)\\,.\\,2 = 2\\log 2 = \\log 4.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{\\theta  \\to 0} \\frac{{1 - \\cos \\theta }}{{{\\theta^2}}} = \\) [AI CBSE , 91; DSSE , 83]','(a)1','(b)2','(c)\\(\\frac{1}{2}\\)','(d)\\(\\frac{1}{4}\\)   ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{\\theta  \\to 0} \\,\\frac{{2\\,{{\\sin }^2}(\\theta/2)}}{{{\\theta ^2}}} = \\frac{1}{2}.\\)Aliter : Apply L-Hospitals rule.  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{\\theta  \\to 0} \\frac{{\\sin 3\\theta  - \\sin \\theta}}{{\\sin \\theta }} = \\)','(a)1','(b)2','(c)1/3','(d)3/2   ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{\\theta  \\to 0} \\,[3 - 4\\,{\\sin ^2}\\theta ] - 1 =2.\\)Aliter : \\(\\mathop {\\lim }\\limits_{\\theta  \\to 0} \\,\\frac{{\\sin 3\\theta  - \\sin\\theta }}{{\\sin \\theta }} = \\mathop {\\lim }\\limits_{\\theta  \\to 0} \\,\\frac{{\\sin3\\theta }}{{\\sin \\theta }} - \\mathop {\\lim }\\limits_{\\theta  \\to 0} \\,\\frac{{\\sin\\theta }}{{\\sin \\theta }}\\)\\(= \\frac{3}{1} - 1 = 2.\\)You may also apply L-Hospital rule.   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\tan x - \\sin x}}{{{x^3}}} = \\) ','(a)\\(\\frac{1}{2}\\)','(b)\\( - \\frac{1}{2}\\)','(c)\\(\\frac{2}{3}\\)','(d)None of these  ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{\\tan x - \\sin x}}{{{x^3}}} =\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{\\sin x - \\sin x\\,\\cos x}}{{{x^3}\\cosx}}\\)    \\(= \\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{\\sin x\\,\\left( {2\\,\\,{{\\sin}^2}\\frac{x}{2}} \\right)}}{{{x^3}\\,\\cos x}} = \\mathop {\\lim }\\limits_{x \\to 0}\\,\\left[ {\\frac{{\\sin x}}{x}.\\frac{2}{{\\cos x}}.\\frac{{{{\\sin}^2}\\frac{x}{2}}}{{{{\\left( {\\frac{x}{2}} \\right)}^2}}}.\\frac{1}{4}} \\right] =\\frac{1}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{1 - \\cos x}}{x} = \\) ','(a)0','(b)\\(\\frac{1}{2}\\)','(c)\\(\\frac{1}{3}\\)','(d)None of these  ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{2\\,{{\\sin }^2}(x/2)}}{x} = 0.\\)Aliter : Apply L-Hospitals rule.   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 1} \\frac{{1 - \\sqrt x }}{{{{({{\\cos }^{ -1}}x)}^2}}} = \\) ','(a)1','(b)\\(\\frac{1}{2}\\)','(c)\\(\\frac{1}{4}\\)','(d)None of these  ','3','FUNCTION, L, C, D','2','Answer =  (c)Put \\({\\cos ^{ - 1}}x = y\\) and \\(x \\to 1\\, \\Rightarrow \\,\\,y \\to 0.\\)\\(\\mathop {\\lim }\\limits_{x \\to 1} \\,\\frac{{1 - \\sqrt x }}{{{{({{\\cos }^{ -1}}x)}^2}}} = \\mathop {\\lim }\\limits_{y \\to 0} \\,\\frac{{1 - \\sqrt {\\cos y}}}{{{y^2}}}\\)Now rationalizing it, we get \\(\\mathop {\\lim }\\limits_{y \\to 0} \\,\\frac{{(1 -\\cos y)}}{{{y^2}(1 + \\sqrt {\\cos y} )}}\\)\\(= \\mathop {\\lim }\\limits_{y \\to 0} \\,\\frac{{1 - \\cos y}}{{{y^2}}}\\,.\\,\\mathop{\\lim }\\limits_{y \\to 0} \\,\\frac{1}{{1 + \\sqrt {\\cos y} }} = \\frac{1}{2} \\times\\frac{1}{2} = \\frac{1}{4}.\\) ',CURRENT_TIMESTAMP);
