INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to \\pi /4} \\frac{{\\sqrt 2 \\cos x - 1}}{{\\cot x -1}} = \\) ','(a)\\(\\frac{1}{{\\sqrt 2 }}\\)','(b)\\(\\frac{1}{2}\\)','(c) \\(\\frac{1}{{2\\sqrt 2 }}\\)','(d) 1    ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to \\pi /4} \\,\\frac{{(\\sqrt 2  - \\sec x)\\,\\cosx\\,(1 + \\cot x)}}{{\\cot x\\,[2 - {{\\sec }^2}x]}}\\)\\(= \\mathop {\\lim }\\limits_{x \\to \\pi /4} \\frac{{\\sin x\\,(1 + \\cot x)}}{{(\\sqrt 2 + \\sec x)}} = \\frac{{\\frac{1}{{\\sqrt 2 }}(2)}}{{\\sqrt 2  + \\sqrt 2 }} =\\frac{1}{2}.\\)Aliter : Apply L-Hospitals rule. ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to a} \\frac{{\\cos x - \\cos a}}{{\\cos x - \\cot a}} =\\) ','(a)\\(\\frac{1}{2}{\\sin ^3}a\\)','(b)\\(\\frac{1}{2}{\\rm{cose}}{{\\rm{c}}^2}a\\)','(c)\\({\\sin ^3}a\\)','(d)\\({\\rm{cose}}{{\\rm{c}}^3}a\\)   ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to a} \\,\\frac{{\\cos x - \\cos a}}{{\\cot x - \\cota}} = \\mathop {\\lim }\\limits_{x \\to a} \\,\\left( {\\frac{{ - \\sin x}}{{ - \\cose{c^2}x}}} \\right) = \\mathop {\\lim }\\limits_{x \\to a} {\\sin ^3}x = {\\sin ^3}a\\).    ',CURRENT_TIMESTAMP);
