INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{e^{\\alpha \\;x}} - {e^{\\beta\\;x}}}}{x} = \\)','(a)\\(\\alpha  + \\beta \\)','(b)\\(\\frac{1}{\\alpha } + \\beta \\)','(c)\\({\\alpha ^2} - {\\beta ^2}\\)','(d)\\(\\alpha  - \\beta \\)    ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{{e^{\\alpha x}} - {e^{\\betax}}}}{x} = \\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{{e^{\\alpha x}} - 1 -{e^{\\beta x}} + 1}}{x}\\)\\( = \\alpha \\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{{e^{\\alpha x}} -1}}{{\\alpha x}} - \\beta \\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{{e^{\\beta x}} -1}}{{\\beta x}}\\)\\(= \\alpha .1 - \\beta .1 = \\alpha  - \\beta .\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to a} \\frac{{({x^{ - 1}} - {a^{ - 1}})}}{{x - a}} =\\) ','(a)\\({x_{n + 1}} = \\sqrt {2 + {x_n}} ,\\;n \\ge 1,\\;\\)','(b)\\(\\frac{{ - 1}}{a}\\)','(c)\\(\\frac{1}{{{a^2}}}\\)','(d)\\(\\frac{{ - 1}}{{{a^2}}}\\)  ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {\\lim }\\limits_{x \\to a} \\,\\frac{{(1/x) - (1/a)}}{{x - a}} =\\mathop {\\lim }\\limits_{x \\to a} \\,\\frac{{a - x}}{{ax\\,(x - a)}} = \\mathop {\\lim}\\limits_{x \\to a} \\,\\frac{{ - 1}}{{ax}} = \\frac{{ - 1}}{{{a^2}}}\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to \\infty } {\\left( {\\frac{{x + 2}}{{x + 1}}}\\right)^{x + 3}}\\) is ','(a)\\(1\\)','(b)\\(e\\)','(c)\\({e^2}\\)','(d)\\({e^3}\\) ','2','FUNCTION, L, C, D','2','Answer =  (b)Let \\(A = \\mathop {\\lim }\\limits_{x \\to \\infty } \\,{\\left( {\\frac{{x + 2}}{{x+ 1}}} \\right)^{x + 3}}\\)\\( = \\mathop {\\lim }\\limits_{x \\to \\infty } \\,{\\left( {1 + \\frac{1}{{x + 1}}}\\right)^{x + 3}}\\)\\(= \\mathop {\\lim }\\limits_{x \\to \\infty } \\,{\\left[ {{{\\left( {1 + \\frac{1}{{x +1}}} \\right)}^{x + 1}}} \\right]^{\\frac{{\\,(x + 3)}}{{(x + 1)}}}} = e\\)\\(\\left\\{ {\\,\\,\\mathop {\\lim }\\limits_{x \\to \\infty } \\,{{\\left( {1 +\\frac{1}{{x + 1}}} \\right)}^{x + 1}} = e} \\right.\\)and \\(\\mathop {\\lim}\\limits_{x \\to \\infty } \\frac{{\\,(x + 3)}}{{(x + 1)}} = \\left. {\\mathop {\\lim}\\limits_{x \\to \\infty } \\frac{{\\,\\left\\{ {1 + (3/x)} \\right\\}}}{{\\left\\{ {1 +(1/x)} \\right\\}}} = 1} \\right\\}\\).   ',CURRENT_TIMESTAMP);
