INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{e^{\\tan x}} - {e^x}}}{{\\tan x - x}} =\\) ','(a)1','(b)\\(e\\)','(c)\\({e^{ - 1}}\\)','(d)\\(\\frac{1}{2}\\) ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{{e^{\\tan x}} - {e^x}}}{{\\tan x -x}} = \\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{{e^x}[{e^{\\tan x - x}} -1]}}{{\\tan x - x}}\\)    \\(= \\mathop {\\lim }\\limits_{x \\to 0} \\,{e^x}\\,.\\mathop {\\lim }\\limits_{x \\to 0}\\,\\frac{{{e^{\\tan x - x}} - 1}}{{\\tan x - x}} = {e^0} \\times 1 = 1.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\sqrt {\\frac{{x - \\sin x}}{{x + {{\\cos }^2}x}}} \\), then \\(\\mathop{\\lim }\\limits_{x \\to \\infty } f(x)\\)is','(a)0','(b)\\(\\infty \\)','(c)1','(d)None of these    ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\,\\,f(x) = \\,\\mathop {\\lim}\\limits_{x \\to \\infty } \\,\\sqrt {\\frac{{x - \\sin x}}{{x + {{\\cos }^2}x}}}  =\\mathop {\\lim }\\limits_{x \\to \\infty } \\,\\,\\sqrt {\\frac{{1 - \\frac{{\\sinx}}{x}}}{{1 + \\frac{{{{\\cos }^2}x}}{x}}}} \\)\\( = \\sqrt {\\frac{{1 - 0}}{{1 + 0}}}  = 1\\), \\(\\left( {\\,\\,\\,\\frac{{\\sin x}}{x}\\to 0,\\frac{{{{\\cos }^2}x}}{x}\\, \\to 0\\,\\,{\\rm{as }}x \\to \\infty } \\right)\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to  - 1} \\frac{{\\sqrt \\pi   - \\sqrt {{{\\cos }^{ -1}}x} }}{{\\sqrt {x + 1} }}\\)is given by','(a)\\(\\frac{1}{{\\sqrt \\pi  }}\\)','(b)\\(\\frac{1}{{\\sqrt {2\\pi } }}\\)','(c)1','(d)0 ','2','FUNCTION, L, C, D','2','Answer =  (b)Put \\({\\cos ^{ - 1}}x = y.\\) So if \\(x \\to  - 1,\\,\\,y \\to \\pi \\)\\(\\therefore \\,\\,\\,\\mathop {\\lim }\\limits_{x \\to  - 1} \\,\\frac{{\\sqrt \\pi   -\\sqrt {{{\\cos }^{ - 1}}x} }}{{\\sqrt {x + 1} }} = \\mathop {\\lim }\\limits_{y \\to\\pi } \\,\\frac{{\\sqrt \\pi   - \\sqrt y }}{{\\sqrt {1 + \\cos y} }}\\)\\(= \\mathop {\\lim }\\limits_{y \\to \\pi } \\,\\frac{{\\sqrt \\pi   - \\sqrt y }}{{\\sqrt2 \\,\\cos \\,(y/2)}}\\, = \\mathop {\\lim }\\limits_{y \\to \\pi } \\,\\,\\frac{{\\sqrt \\pi  - \\sqrt y }}{{\\sqrt 2 \\,\\sin \\,\\left( {\\frac{\\pi }{2} - \\frac{y}{2}}\\right)}}\\frac{{\\left( {\\frac{\\pi }{2} - \\frac{y}{2}} \\right)}}{{\\left({\\frac{\\pi }{2} - \\frac{y}{2}} \\right)}}\\)\\(= \\mathop {\\lim }\\limits_{y \\to \\pi } \\,\\frac{1}{{\\frac{{\\sqrt 2 }}{2}(\\sqrt\\pi   + \\sqrt y )}}.\\frac{1}{{\\frac{{\\sin \\,\\left( {\\frac{\\pi }{2} - \\frac{y}{2}}\\right)}}{{\\left( {\\frac{\\pi }{2} - \\frac{y}{2}} \\right)}}}} = \\frac{1}{{\\sqrt{2\\pi } }}.\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\left[ {\\sqrt {x + \\sqrt {x + \\sqrt x }}  - \\sqrt x } \\right]\\)is equal to   ','(a)0','(b)\\(\\frac{1}{2}\\)','(c)\\(\\frac{1}{p} - \\frac{1}{{p - 1}}\\)','(d)\\({e^4}\\)   ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\,\\left[ {\\sqrt {x + \\sqrt {x +\\sqrt x } }  - \\sqrt x } \\right] = \\mathop {\\lim }\\limits_{x \\to \\infty }\\frac{{x + \\sqrt {x + \\sqrt x }  - x}}{{\\sqrt {x + \\sqrt {x + \\sqrt x } }  +\\sqrt x }}\\) \\(= \\mathop {\\lim }\\limits_{x \\to \\infty } \\frac{{\\sqrt {x + \\sqrt x } }}{{\\sqrt{x + \\sqrt {x + \\sqrt x } }  + \\sqrt x }} = \\mathop {\\lim }\\limits_{x \\to \\infty} \\,\\frac{{\\sqrt {1 + {x^{ - 1/2}}} }}{{\\sqrt {1 + \\sqrt {{x^{ - 1}} + {x^{ -3/2}}} }  + 1}} = \\frac{1}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\frac{2}{{x - 3}},\\;g(x) = \\frac{{x - 3}}{{x + 4}}\\) and \\(h(x) =  -\\frac{{2(2x + 1)}}{{{x^2} + x - 12}},\\) then \\(\\mathop {\\lim }\\limits_{x \\to 3}[f(x) + g(x) + h(x)]\\) is ','(a)\\( - 2\\)','(b)\\( - 1\\)','(c) \\(- \\frac{2}{7}\\)','(d) 0 ','3','FUNCTION, L, C, D','2','Answer =  (c)We have \\(f(x) + g(x) + h(x) = \\frac{{{x^2} - 4x + 17 - 4x - 2}}{{{x^2} + x -12}}\\)\\(= \\frac{{{x^2} - 8x + 15}}{{{x^2} + x - 12}} = \\frac{{(x - 3)\\,(x - 5)}}{{(x -3)\\,(x + 4)}}\\) \\(\\therefore \\,\\,\\mathop {\\lim }\\limits_{x \\to 3} \\,[f(x) + g(x) + h(x)] = \\mathop{\\lim }\\limits_{x \\to 3} \\,\\,\\frac{{(x - 3)\\,\\,(x - 5)}}{{(x - 3)\\,\\,(x + 4)}} = - \\frac{2}{7}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\mathop {\\lim }\\limits_{x \\to 0} {\\left( {\\frac{{{a^x} + {b^x} +{c^x}}}{3}} \\right)^{2/x}}\\); \\((a,\\;b,\\;c > 0)\\) is   ','(a)\\({(abc)^3}\\)','(b)\\(abc\\)','(c)\\({(abc)^{1/3}}\\)','(d)None of these    ','4','FUNCTION, L, C, D','2','Answer =  (d)Let \\(y = \\mathop {\\lim }\\limits_{x \\to 0} \\,{\\left( {\\frac{{{a^x} + {b^x} +{c^x}}}{3}} \\right)^{2/x}}\\)\\(= \\mathop {\\lim }\\limits_{h \\to 0} h\\,\\sin 1/h = 0 \\times ( - 1 \\le \\sin 1/h\\le 1) = 0\\)\\(= 2\\,\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{\\log \\,({a^x} + {b^x} + {c^x}) -\\log 3}}{x}\\)Now applying L-Hospitals rule, we have\\(\\log y = \\log \\,{(abc)^{2/3}}\\, \\Rightarrow \\,\\,y = {(abc)^{2/3}}\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of  \\(\\mathop {\\lim }\\limits_{x \\to 2} \\frac{{\\sqrt {1 + \\sqrt {2 + x}}  - \\sqrt 3 }}{{x - 2}}\\)is ','(a)\\(\\mathop {\\lim }\\limits_{x \\to \\frac{\\pi }{2}} \\frac{{\\left[ {1 - \\tan\\left( {\\frac{x}{2}} \\right)} \\right]\\,[1 - \\sin x]}}{{\\left[ {1 + \\tan \\left({\\frac{x}{2}} \\right)} \\right]\\,{{[\\pi  - 2x]}^3}}}\\)','(b)\\(\\frac{1}{{4\\sqrt 3 }}\\)','(c)0','(d)None of these  ','1','FUNCTION, L, C, D','2','Answer =  (a)We have \\(\\mathop {\\lim }\\limits_{x \\to 2} \\,\\frac{{\\sqrt {1 + \\sqrt {2 + x}}  - \\sqrt 3 }}{{x - 2}}\\)\\(= \\mathop {\\lim }\\limits_{x \\to 2} \\,\\,\\frac{{1 + \\sqrt {2 + x}  - 3}}{{(\\sqrt{1 + \\sqrt {2 + x}  + \\sqrt 3 )\\,\\,(x - 2)} }}\\)\\(= \\mathop {\\lim }\\limits_{x \\to 2} \\,\\,\\frac{{\\sqrt {2 + x}  - 2}}{{(\\sqrt {1 +\\sqrt {2 + x}  + \\sqrt 3 )\\,\\,(x - 2)} }}\\)\\(= \\mathop {\\lim }\\limits_{x \\to 2} \\,\\,\\frac{{(x - 2)}}{{(\\sqrt {1 + \\sqrt {2 +x} }  + \\sqrt 3 )\\,\\,(\\sqrt {2 + x}  + 2)\\,\\,(x - 2)}}\\)\\(= \\frac{1}{{(2\\sqrt 3 )\\,4}} = \\frac{1}{{8\\sqrt 3 }}.\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\sqrt {1 - \\cos {x^2}}}}{{1 - \\cos x}}\\)is    ','(a)\\(\\frac{1}{2}\\)','(b)\\(2\\)','(c)\\(\\sqrt 2 \\)','(d)None of these   ','3','FUNCTION, L, C, D','2','Answer =  (c)We have \\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{\\sqrt {1 - \\cos {x^2}}}}{{1 - \\cos x}} = \\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{\\sqrt {2\\,{{\\sin}^2}({x^2}/2)} }}{{2\\,{{\\sin }^2}(x/2)}}\\)  \\(= \\frac{1}{{\\sqrt 2 }}\\,\\mathop {\\lim }\\limits_{x \\to 0} \\,\\left({\\frac{{\\frac{{\\sin \\,({x^2}/2)}}{{{x^2}/2}}}}{{{{\\left( {\\frac{{\\sin\\,(x/2)}}{{x/2}}} \\right)}^2}}}} \\right).\\frac{{{x^2}/2}}{{{x^2}/4}} = \\sqrt 2 .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\mathop {\\lim }\\limits_{x \\to {0^ + }} {x^m}{(\\log x)^n},\\;m,\\;n\\in N\\)is ','(a)0','(b)\\(\\frac{m}{n}\\)','(c)\\(mn\\)','(d)None of these    ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to 0 + } \\,{x^m}\\,{(\\log x)^n} = \\mathop {\\lim}\\limits_{x \\to 0 + } \\,\\frac{{{{(\\log x)}^n}}}{{{x^{ - m}}}}\\) \\(\\left({{\\rm{Form}}\\frac{\\infty }{\\infty }} \\right)\\)\\(\\log \\,\\,P = \\int_0^1 {} \\log x\\,dx = (x\\,\\log x - x)_0^1 = ( - 1)\\)              (By L-Hospitals rule)\\( = \\mathop {\\lim }\\limits_{x \\to 0 + } \\,\\frac{{n\\,{{(\\log x)}^{n -1}}}}{{ - m{x^{ - m}}}}\\,\\)\\(\\left( {{\\rm{Form}}\\frac{\\infty }{\\infty }} \\right)\\)\\( = \\mathop {\\lim }\\limits_{x \\to 0 + } \\,\\frac{{n\\,(n - 1)\\,{{(\\logx)}^{(n - 2)}}\\frac{1}{x}}}{{{{( - m)}^2}{x^{ - m - 1}}}}\\)     (By L-Hospitalsrule)\\( = \\mathop {\\lim }\\limits_{x \\to 0 + } \\,\\frac{{n\\,(n - 1)\\,{{(\\log x)}^{n- 2}}}}{{{m^2}{x^{ - m}}}}\\,\\)\\(\\left( {{\\rm{Form}}\\frac{\\infty }{\\infty }} \\right)\\).............................................\\(= \\mathop {\\lim }\\limits_{x \\to 0 + } \\,\\frac{{n\\,\\,!}}{{{{( - m)}^n}{x^{ -m}}}} = 0\\)(Differentiating \\({N^r}\\) and \\({D^r}\\) n times).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\frac{{\\logx}}{{{x^n}}},\\;n > 0\\)is    ','(a)\\(0\\)','(b)\\(1\\)','(c)\\(\\frac{1}{n}\\)','(d)\\(\\frac{1}{{n!}}\\)   ','1','FUNCTION, L, C, D','2','Answer =  (a)\\({\\rm{ }}\\mathop {\\lim }\\limits_{x \\to \\infty } \\,\\frac{{\\log x}}{{{x^n}}} =\\mathop {\\lim }\\limits_{x \\to \\infty } \\,\\frac{1}{{n{x^n}}} = 0\\)           (ByL-Hospitals rule) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\mathop {\\lim }\\limits_{x \\to a} \\frac{{\\log (x - a)}}{{\\log({e^x} - {e^a})}}\\)is  ','(a)1','(b)-1','(c)0','(d)None of these   ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {{\\rm{lim}}}\\limits_{{\\rm{x}} \\to a} \\,\\,\\frac{{\\log \\,(x -a)}}{{\\log \\,({e^x} - {e^a})}} = \\mathop {{\\rm{lim}}}\\limits_{{\\rm{x}} \\to a}\\,\\,\\frac{{{e^x} - {e^a}}}{{(x - a)\\,{e^x}}}\\), \\(\\left( {{\\rm{Form}}\\frac{0}{0}}\\right)\\)\\(= \\mathop {\\lim }\\limits_{x \\to a} \\,\\,\\frac{{{e^x}}}{{\\left\\{ {(x - a)\\,{e^x}+ {e^x}} \\right\\}}} = \\frac{{{e^a}}}{{{e^a}}} = 1.\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{{(1 + x)}^{1/x}} - e +\\frac{1}{2}ex}}{{{x^2}}}\\)is     ','(a)\\(\\frac{{11e}}{{24}}\\)','(b)\\(\\frac{{ - 11e}}{{24}}\\)','(c)\\(\\frac{e}{{24}}\\)','(d)None of these ','1','FUNCTION, L, C, D','2','Answer =  (a)\\({(1 + x)^{1/x}} = {e^{\\frac{1}{x}\\log \\,(1 + x)}} = {e^{\\frac{1}{x}\\,\\left({x - \\frac{{{x^2}}}{2} + \\frac{{{x^3}}}{3}\\, - .......} \\right)}}\\)\\(= {e^{1 - \\frac{x}{2} + \\frac{{{x^2}}}{3}\\, - ......}} = e\\,{e^{ -\\,\\frac{x}{2} + \\frac{{{x^2}}}{3}\\, - \\,.....}}\\)\\(= e\\,\\left[ {1 + \\left( { - \\frac{x}{2} + \\frac{{{x^2}}}{3} - .....} \\right) +\\frac{1}{{2\\,\\,!}}\\,{{\\left( { - \\frac{x}{2} + \\frac{{{x^2}}}{3}\\, - \\,.....}\\right)}^2} + ...} \\right]\\)\\(= e\\,\\left[ {1 - \\frac{x}{2} + \\frac{{11}}{{24}}{x^2} - ....} \\right]\\)    \\(\\therefore \\,\\,\\,\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{{{(1 + x)}^{1/x}} -e + \\frac{{ex}}{2}}}{{{x^2}}} = \\frac{{11e}}{{24}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to \\pi /2} \\left[ {x\\tan x - \\left( {\\frac{\\pi }{2}}\\right)\\sec x} \\right] =\\) ','(a)1','(b)-1','(c)0','(d)None of these   ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to \\pi /2} \\,\\left[ {x\\tan x - \\left( {\\frac{\\pi}{2}} \\right)\\,\\sec x} \\right]\\)\\( = \\mathop {\\lim }\\limits_{x \\to \\pi /2} \\,\\,\\frac{{2x\\,\\,\\sin x - \\pi}}{{2\\,\\cos x}}\\)\\( = \\mathop {\\lim }\\limits_{x \\to \\pi /2} \\,\\,\\frac{{[2\\,\\sin x+ 2x\\cos x]}}{{ - 2\\,\\sin x}} =  - 1\\),\\(\\left[ {{\\rm{form}}\\frac{0}{0}} \\right],\\)(By L-Hospitals rule).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\left[ {\\frac{{\\sin (x + a) + \\sin (a - x) -2\\sin a}}{{x\\sin x}}} \\right] =\\)  ','(a)\\(\\sin a\\)','(b)\\(\\cos a\\)','(c)\\( - \\sin a\\)','(d)\\(\\frac{1}{2}\\cos a\\) ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,2\\,\\sin \\,a\\,.\\,\\frac{{(\\cos x -1)}}{{x\\sin x}}\\)\\( =  - 2\\,\\sin a\\,.\\,\\frac{{(1 - \\cos x)}}{{{x^2}}}\\,.\\,\\left({\\frac{x}{{\\sin x}}} \\right)\\) \\(= \\mathop {\\lim }\\limits_{x \\to 0} \\,\\, - 2\\sin a\\,.\\,\\frac{{2\\,{{\\sin}^2}(x/2)}}{{4\\,{{\\left( {\\frac{x}{2}} \\right)}^2}\\,\\left( {\\frac{{\\sin x}}{x}}\\right)}} =  - \\sin a.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} {\\left( {\\frac{{1 + 5{x^2}}}{{1 + 3{x^2}}}}\\right)^{1/{x^2}}} = \\)     ','(a)\\({e^2}\\)','(b)\\(e\\)','(c)\\({e^{ - 2}}\\)','(d)\\({e^{ - 1}}\\)  ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,{\\left( {\\frac{{1 + 5{x^2}}}{{1 +3{x^2}}}} \\right)^{1/{x^2}}} = \\frac{{\\mathop {\\lim }\\limits_{x \\to 0}\\,\\,{{\\left[ {{{(1 + 5{x^2})}^{1/5{x^2}}}} \\right]}^5}}}{{\\mathop {\\lim}\\limits_{x \\to 0} \\,\\,{{\\left[ {{{(1 + 3{x^2})}^{1/3{x^2}}}} \\right]}^3}}} =\\frac{{{e^5}}}{{{e^3}}} = {e^2}\\).\\([\\,\\,\\,\\mathop {\\lim }\\limits_{x \\to 0} \\,{(1 + x)^{1/x}} = e]\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\frac{{(2x - 3)(3x - 4)}}{{(4x - 5)(5x- 6)}} = \\) ','(a)0','(b)1/10','(c)1/5','(d)3/10    ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\,\\,\\frac{{(2x - 3)\\,\\,(3x -4)}}{{(4x - 5)\\,\\,(5x - 6)}}\\)\\(= \\mathop {\\lim }\\limits_{x \\to \\infty } \\frac{{{x^2}\\left( {2 - \\frac{3}{x}}\\right)\\,\\left( {3 - \\frac{4}{x}} \\right)}}{{{x^2}\\left( {4 - \\frac{5}{x}}\\right)\\,\\left( {5 - \\frac{6}{x}} \\right)}} = \\frac{6}{{20}} = \\frac{3}{{10}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\frac{{\\sin ({e^{x - 2}} - 1)}}{{\\log (x - 1)}},\\)then \\(\\mathop{\\lim }\\limits_{x \\to 2} f(x)\\)is given by  ','(a)-2','(b)-1','(c)0','(d)1  ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {\\lim }\\limits_{x \\to 2} \\,\\,f(x) = \\mathop {\\lim }\\limits_{x \\to 2}\\,\\frac{{\\sin \\,({e^{x - 2}} - 1)}}{{\\log \\,(x - 1)}}\\)\\( = \\mathop {\\lim }\\limits_{t \\to 0} \\,\\,\\frac{{\\sin \\,({e^t} - 1)}}{{\\log \\,(1+ t)}}\\), {Putting \\(x = 2 + t\\} \\)\\(= \\mathop {\\lim }\\limits_{t \\to 0} \\,\\,\\frac{{\\sin \\,({e^t} - 1)}}{{{e^t} -1}}.\\frac{{{e^t} - 1}}{t}.\\frac{t}{{\\log \\,(1 + t)}}\\)\\(= \\mathop {\\lim }\\limits_{t \\to 0} \\,\\,\\frac{{\\sin \\,({e^t} - 1)}}{{{e^t} -1}}.\\left( {\\frac{1}{{1\\,\\,!}} + \\frac{t}{{2\\,\\,!}} + ...} \\right) \\times \\left[{\\frac{1}{{\\left( {1 - \\frac{1}{2}t + \\frac{1}{3}{t^2} - ...} \\right)}}} \\right]\\)\\(= 1\\,\\,.\\,\\,1\\,\\,.\\,\\,1 = 1,\\,\\,\\,\\,(\\,\\,{\\rm{As}}\\,\\,t \\to 0,\\,\\,{e^t} - 1 \\to0).\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to \\infty } (\\sqrt {{x^2} + 8x + 3}  - \\sqrt {{x^2}+ 4x + 3} ) = \\)  ','(a)0','(b)\\(\\infty \\)','(c)2','(d)\\(\\frac{1}{2}\\)  ','3','FUNCTION, L, C, D','2','Answer =  (c) On rationalization \\(\\mathop {\\lim }\\limits_{x \\to \\infty }\\,\\frac{{4x}}{{(\\sqrt {{x^2} + 8x + 3}  + \\sqrt {{x^2} + 4x + 3} }}\\)    \\(= \\mathop {\\lim }\\limits_{x \\to \\infty } \\,\\frac{4}{{\\left( {\\sqrt {1 +\\frac{8}{x} + \\frac{3}{{{x^2}}}}  + \\sqrt {1 + \\frac{4}{x} + \\frac{3}{{{x^2}}}} }\\right)}} = 2.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(\\mathop {\\lim }\\limits_{x \\to 5} \\frac{{{x^k} - {5^k}}}{{x - 5}} = ),then the positve integral value of k is    ','(a)3','(b)4','(c)5','(d)6    ','2','FUNCTION, L, C, D','2','Answer =  (b)We know that, \\(\\mathop {\\lim }\\limits_{x \\to a} \\,\\frac{{{x^n} - {a^n}}}{{x -a}} = n\\,\\,{a^{n - 1}}\\)\\(\\therefore{}\\)\\(\\mathop {\\lim }\\limits_{x \\to 5} \\frac{{{x^k} - {5^k}}}{{x -5}} = k{(5)^{k - 1}}\\); But given, \\(\\mathop {\\lim }\\limits_{x \\to 5}\\,\\frac{{{x^k} - {5^k}}}{{x - 5}} = Answer = ),\\(\\therefore{}\\) \\(k{(5)^{k - 1}} = Answer = );  \\(k\\,{(5)^{k - 1}} = 4\\,{(5)^{4 -1}},\\,\\,\\,\\,\\therefore \\,\\,\\,k = 4\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ \\begin{array}{l}x,\\;\\;{\\rm{if }}x{\\rm{ is rational }}\\\\- x,\\;{\\rm{if }}x{\\rm{ is irrational}}\\end{array} \\right.,\\) then \\(\\mathop {\\lim }\\limits_{x \\to 0} f(x)\\)is ','(a)Equal to 0','(b)Equal to 1','(c)Equal to -1','(d)Indeterminate    ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to {0^ - }} f(x) = \\mathop {\\lim }\\limits_{h \\to0} f(0 - h) = \\mathop {\\lim }\\limits_{h \\to 0} f(0 - h) = 0\\)and \\(\\mathop {\\lim }\\limits_{x \\to {0^ + }} f(x) = \\mathop {\\lim }\\limits_{h\\to 0} f(0 + h) = \\mathop {\\lim }\\limits_{h \\to 0} \\,\\,\\, - (0 + h) = 0\\)\\(\\therefore \\,\\,\\,\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,f(x) = 0\\), \\(\\left({\\mathop {\\lim }\\limits_{x \\to {0^ - }} f(x) = \\mathop {\\lim }\\limits_{x \\to {0^+ }} f(x)} \\right)\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{x{e^x} - \\log (1 + x)}}{{{x^2}}}\\)equals     ','(a)\\(\\frac{2}{3}\\)','(b)\\(\\frac{1}{3}\\)','(c)\\(\\frac{1}{2}\\)','(d)\\(\\frac{3}{2}\\)    ','4','FUNCTION, L, C, D','2','Answer =  (d)Let \\(y = \\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{x\\,{e^x} - \\log \\,(1 +x)}}{{{x^2}}}\\), \\(\\left( {\\frac{0}{0}\\,{\\rm{form}}} \\right)\\)Applying L-Hospitals rule,\\(y = \\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{{e^x} + x\\,{e^x} - \\frac{1}{{1+ x}}}}{{2x}}\\)\\(y = \\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{1}{2}\\,\\left[ {{e^x} + {e^x} +x\\,{e^x} + \\frac{1}{{{{(1 + x)}^2}}}} \\right]\\)\\(\\left( {\\frac{0}{0}\\,{\\rm{form}}} \\right),\\)\\(y = \\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{1}{2}\\,[1 + 1 + 0 + 1] =\\frac{3}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\mathop {\\lim }\\limits_{x \\to  - \\infty } \\frac{{\\sqrt {4{x^2} +5x + 8} }}{{4x + 5}}\\)is','(a) - 1/2','(b) 0','(c) 1/2','(d) 1 ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x\\, \\to \\, - \\infty } \\,\\frac{{\\sqrt {4{x^2} + 5x +8} }}{{4x + 5}}\\)\\( = \\mathop {\\lim }\\limits_{h \\to 0} \\,\\,\\frac{{\\sqrt {4\\,{{( -1/h)}^2} + 5\\,( - 1/h) + 8} }}{{4\\,( - 1/h) + 5}}\\)  \\(= \\mathop {\\lim }\\limits_{h \\to 0} \\,\\,\\frac{{(1/h)\\sqrt {4\\, - 5h + 8{h^2}}}}{{(1/h)\\,( - \\,4 + 5h)}} = \\frac{{\\sqrt 4 }}{{ - 4}} =  - \\frac{1}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to \\infty } {\\left ','(a)\\({e^{1/m}}\\)','(b)\\({e^{ - 1/m}}\\)','(c)\\({e^m}\\)','(d)\\({m^e}\\) ','1','FUNCTION, L, C, D','2','Answer =  (a)Let \\(y = \\mathop {\\lim }\\limits_{x \\to \\,\\infty } \\,{\\left( {1 +\\frac{1}{{mx}}} \\right)^x} = \\mathop {\\lim }\\limits_{x \\to \\,\\infty } \\,{\\left({1 + \\frac{1}{{mx}}} \\right)^{mx \\cdot \\frac{1}{m}}}\\)  \\(\\Rightarrow \\,\\,y = {e^{1/m}},\\,\\,\\,\\left( {\\mathop {\\lim }\\limits_{x \\to\\,\\infty } \\,{{\\left( {1 + \\frac{1}{x}} \\right)}^x} = e} \\right).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Let the function f be defined by the equation \\(f(x) = \\left\\{ \\begin{array}{l}3x\\;\\;\\;\\;\\;\\;{\\rm{if}}\\;0 \\le x \\le 1\\\\5 - 3x\\;\\;{\\rm{if}}\\;{\\rm{1}} < x \\le 2\\end{array} \\right.,\\)then  ','(a)\\(\\mathop {\\lim }\\limits_{x \\to 1} f(x) = f(1)\\)','(b)\\(\\mathop {\\lim}\\limits_{x \\to 1} f(x) = 3\\)','(c)\\(\\mathop {\\lim }\\limits_{x \\to 1} f(x) = 2\\)','(d)\\(\\mathop {\\lim }\\limits_{x\\to 1} f(x)\\)does not exist ','4','FUNCTION, L, C, D','2','Answer =  (d)L.H.L.\\( = \\mathop {\\lim }\\limits_{x \\to 1 - 0} f(x) = \\mathop {\\lim}\\limits_{h \\to 0} \\,\\,(1 - h) = \\mathop {\\lim }\\limits_{h \\to 0} \\,\\,3(1 - h)\\)\\(= \\mathop {\\lim }\\limits_{h \\to 0} \\,\\,(3 - 3h) = 3 - 3\\,.\\,0 = 3\\)R.H.L.\\( = \\mathop {\\lim }\\limits_{x \\to 1 + 0} f(x) = \\mathop {\\lim\\,\\,}\\limits_{h \\to 0} \\,f\\,(1 + h) = \\mathop {\\lim }\\limits_{h \\to 0} \\,\\,[5 -3(1 + h)]\\)\\(= \\mathop {\\lim }\\limits_{h \\to 0} \\,\\,(2 - 3h) = 2 - 3\\,.\\,0 = 2\\)Hence \\(\\mathop {\\lim }\\limits_{x \\to 1} \\,\\,f(x)\\) does not exist.   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of the limit of \\(\\frac{{{x^3} - 8}}{{{x^2} - 4}}\\)as x tends to2is   ','(a)3','(b)\\(\\frac{3}{2}\\)','(c)1','(d)0   ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to 2} \\,\\,\\frac{{{x^3} - 8}}{{{x^2} -4}}\\),\\(\\left( {\\frac{0}{0}\\,{\\rm{form}}} \\right)\\)Applying L-Hospitals rule, we get  \\(\\mathop {\\lim }\\limits_{x \\to 2} \\,\\,\\frac{{3{x^2}}}{{2x}} = \\mathop {\\lim}\\limits_{x \\to 2} \\,\\,\\frac{{3 \\times 2 \\times 2}}{{2 \\times 2}} = 3.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of the limit of \\(\\frac{{{x^3} - {x^2} - 18}}{{x - 3}}\\)as x tends to3is  ','(a)3','(b)9','(c)18','(d)21  ','4','FUNCTION, L, C, D','2','Answer =  (d)Let \\(y = \\mathop {\\lim }\\limits_{x \\to 3} \\,\\,\\frac{{{x^3} - {x^2} - 18}}{{x- 3}}\\),\\(\\left( {\\frac{0}{0}\\,{\\rm{form}}} \\right)\\)Applying L-Hospitals rule, we get \\(y = \\mathop {\\lim }\\limits_{x \\to 3} \\,\\,3{x^2} - 2x = (27 - 6) = 21.\\)',CURRENT_TIMESTAMP);
