INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(x = 1\\) is equal to    ','(a)\\(\\frac{2}{3}\\)','(b)\\(1\\)','(c)0','(d)\\(\\infty \\)    ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to \\,\\infty } \\,\\,\\frac{{2{x^2} + 3x +4}}{{3{x^2} + 3x + 4}} = \\mathop {\\lim }\\limits_{x \\to \\infty } \\,\\,\\frac{{2 +\\frac{3}{x} + \\frac{4}{{{x^2}}}}}{{3 + \\frac{3}{x} + \\frac{4}{{{x^2}}}}} =\\frac{2}{3}\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\sin \\left( {\\frac{1}{x}} \\right)\\) is    ','(a)0','(b)1','(c)-1','(d)Does not exist  ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {\\lim }\\limits_{x \\to {0^ - }} f(x) = \\mathop {\\lim }\\limits_{h \\to0} f(0 - h)\\)\\(= \\mathop {\\lim }\\limits_{h \\to 0} \\,\\sin \\,\\left( {\\frac{{ - 1}}{h}} \\right) =\\mathop {\\lim }\\limits_{h \\to 0} \\,\\, - \\sin \\frac{1}{h}\\)= - 1(finite number lies between - 1to 1)\\(\\mathop {\\lim }\\limits_{x \\to {0^ + }} f(x) = \\mathop {\\lim }\\limits_{h \\to 0}f(0 + h)\\)\\( = \\mathop {\\lim }\\limits_{h \\to 0} \\,\\,\\sin \\left( {\\frac{1}{h}}\\right)\\)= (finite number lies between 0to 1)\\(\\,\\,\\,\\,\\mathop {\\lim }\\limits_{x \\to {0^ - }} f(x) \\ne \\mathop {\\lim}\\limits_{x \\to {0^ + }} f(x)\\);  \\(\\therefore{}\\)\\(\\mathop {\\lim }\\limits_{x \\to0} \\sin \\left( {\\frac{1}{x}} \\right)\\) does not exist.  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 4} \\left ','(a)3/2','(b)3','(c)2/3','(d)1/3  ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(y = \\mathop {\\lim }\\limits_{x \\to 4} \\left[ {\\frac{{{x^{3/2}} - 8}}{{x -4}}} \\right]\\)\\( = \\mathop {\\lim }\\limits_{x \\to 4} \\,\\left[{\\frac{{{{({x^{1/2}})}^3} - {{(2)}^3}}}{{(\\sqrt x  - 2)(\\sqrt x  + 2)}}}\\right]\\)\\(\\Rightarrow{}\\) \\(y = \\mathop {\\lim }\\limits_{x \\to 4} \\frac{{({x^{1/2}} -2)(x + 4 + 2\\sqrt x )}}{{(\\sqrt x  - 2)(\\sqrt x  + 2)}}\\)\\(\\Rightarrow{}\\) \\(y = \\mathop {\\lim }\\limits_{x \\to 4} \\frac{{(x + 4 + 2\\sqrtx )}}{{(\\sqrt x  + 2)}}\\)\\( = \\frac{{4 + 4 + 2\\sqrt 4 }}{{\\sqrt 4  + 2}}\\)\\( =\\frac{{12}}{4} = 3\\).Trick : Applying L-Hospitals rule, we get\\(\\mathop {\\lim }\\limits_{x \\to 4} \\frac{{\\frac{3}{2}{x^{1/2}}}}{1}\\)\\( =\\frac{3}{2}{(4)^{1/2}}\\) = 3.  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{e^{\\frac{1}{x}}}}}{{{e^{\\left({\\frac{1}{x} + 1} \\right)}}}} = \\)     ','(a)0','(b)1','(c)Does not exist','(d)None of these   ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {{\\rm{lim}}}\\limits_{x \\to 0} \\frac{{{e^{1/x}}}}{{{e^{\\left({\\frac{1}{x} + 1} \\right)}}}} = \\mathop {{\\rm{lim}}}\\limits_{x \\to 0}\\frac{{{e^{1/x}}}}{{{e^{\\frac{1}{x}}}.e}}\\)\\( = \\mathop {{\\rm{lim}}}\\limits_{x\\to 0} \\frac{1}{e} = {e^{ - 1}}\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{x\\cos x - \\log (1 +x)}}{{{x^2}}}\\)is   ','(a)1/2','(b)0','(c)1','(d)None of these  ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{x\\cos x - \\log (1 +x)}}{{{x^2}}}\\),\\(\\left( {\\frac{0}{0}{\\rm{form}}} \\right)\\)Applying L-Hospitals rule, we have   \\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{\\cos x - x\\sin x - \\frac{1}{{x +1}}}}{{2x}}\\)    \\(\\left( {\\frac{0}{0}{\\rm{form}}} \\right),\\)\\(= \\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{ - \\sin x - \\sin x - x\\cos x +\\frac{1}{{{{(x + 1)}^2}}}}}{2} = \\frac{1}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\mathop {\\lim }\\limits_{a \\to 0} \\frac{{\\sin a - \\tan a}}{{{{\\sin}^3}a}}\\)will be ','(a)\\( - \\frac{1}{2}\\)','(b)\\(\\frac{1}{2}\\)','(c)1','(d)-1  ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{a \\to 0} \\frac{{\\sin a - \\tan a}}{{{{\\sin }^3}a}} =\\mathop {\\lim }\\limits_{a \\to 0} \\frac{{\\cos a - 1}}{{{{\\sin }^2}a\\cos a}} =\\mathop {\\lim }\\limits_{a \\to 0} \\frac{{ - (1 - \\cos a)}}{{(1 - {{\\cos}^2}a)(\\cos a)}}\\)\\( = \\mathop {\\lim }\\limits_{a \\to 0} \\left[ { - \\frac{1}{{(1 + \\cos a)\\cos a}}}\\right] =  - \\frac{1}{{(1 + 1)1}} = \\frac{{ - 1}}{2}\\)  ',CURRENT_TIMESTAMP);
