INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 1} \\frac{{1 + \\log x - x}}{{1 - 2x + {x^2}}} =\\)   ','(a)1','(b)-1','(c)0','(d)\\( - \\frac{1}{2}\\)   ','4','FUNCTION, L, C, D','2','Answer =  (d)Applying L-Hospitals rule,\\(\\mathop {{\\rm{lim}}}\\limits_{x \\to 1} \\,\\,\\frac{{1 + \\log x - x}}{{1 - 2x +{x^2}}} = \\mathop {{\\rm{lim}}}\\limits_{x \\to 1} \\,\\,\\frac{{\\frac{1}{x} - 1}}{{ -2 + 2x}} = \\mathop {{\\rm{lim}}}\\limits_{x \\to 1} \\,\\,\\frac{{1 - x}}{{2x(x - 1)}}\\)Again applying L-Hospitals rule, \\(\\mathop {{\\rm{lim}}}\\limits_{x \\to 1}\\frac{{ - 1}}{{4x - 2}} =  - \\frac{1}{2}\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{a^{\\sin x}} - 1}}{{{b^{\\sin x}} - 1}}= \\)  ','(a)\\(\\frac{a}{b}\\)','(b)\\(\\frac{b}{a}\\)','(c)\\(\\frac{{\\log a}}{{\\log b}}\\)','(d)\\(\\frac{{\\log b}}{{\\log a}}\\)    ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {{\\rm{lim}}}\\limits_{x \\to 0} \\frac{{{a^{\\sin x}} - 1}}{{{b^{\\sinx}} - 1}} = \\mathop {{\\rm{lim}}}\\limits_{x \\to 0} \\frac{{{a^{\\sin x}} - 1}}{{\\sinx}} \\times \\frac{{\\sin x}}{{{b^{\\sin x}} - 1}}\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\mathop {\\lim }\\limits_{x \\to 2} \\frac{{{3^{x/2}} - 3}}{{{3^x} -9}}\\) is     ','(a)0','(b)1/3','(c)\\(1/6\\)','(d)\\(\\ln 3\\)   ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to 2} \\,\\left( {\\frac{{{3^{x/2}} - 3}}{{{3^x} -9}}} \\right)\\) \\( = \\mathop {{\\rm{lim}}}\\limits_{x \\to 2} \\,\\,\\left({\\frac{{{3^{x/2}} - 3}}{{{{({3^{x/2}})}^2} - {3^2}}}} \\right)\\)    \\(= {\\log _e}a \\times \\frac{1}{{{{\\log }_e}b}} = \\frac{{\\log a}}{{\\log b}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{{\\sin }^{ - 1}}x - {{\\tan }^{ -1}}x}}{{{x^3}}}\\) is equal to   ','(a)0','(b)1','(c)-1','(d)\\(1/2\\)   ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{{\\sin }^{ - 1}}x - {{\\tan }^{ -1}}x}}{{{x^3}}}\\),  \\(\\left( {\\frac{0}{0}} \\right)\\)Appl\\(= \\mathop {{\\rm{lim}}}\\limits_{x \\to 2} \\,\\frac{1}{{{3^{x/2}} + 3}} =\\frac{1}{6}.\\)ying L-Hospitals rule,\\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\frac{1}{{\\sqrt {1 - {x^2}} }} -\\frac{1}{{1 + {x^2}}}}}{{3{x^2}}}\\)\\(\\left( {\\frac{0}{0}} \\right),\\)\\(= \\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\frac{{ - 1}}{2} \\times \\frac{{ -2x}}{{{{(1 - {x^2})}^{3/2}}}} + \\frac{{2x}}{{{{(1 + {x^2})}^2}}}}}{{6x}}\\) \\(= \\mathop {\\lim }\\limits_{x \\to 0} \\frac{1}{6}\\left[ {\\frac{1}{{{{(1 -{x^2})}^{3/2}}}} + \\frac{2}{{{{(1 + {x^2})}^2}}}} \\right]\\, = \\,\\frac{1}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{x\\tan 2x - 2x\\tan x}}{{{{(1 - \\cos2x)}^2}}}\\) is   ','(a)2','(b)-2','(c)\\(\\frac{1}{2}\\)','(d)\\( - \\frac{1}{2}\\)  ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{x\\tan 2x - 2x\\tan x}}{{{{(1 - \\cos\\,\\,2x)}^2}}}\\)\\(= \\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{x(\\tan \\,\\,2x - 2\\tanx)}}{{{{(2\\,{{\\sin }^2}x)}^2}}} = \\mathop {\\lim }\\limits_{x \\to 0}\\,\\,\\frac{1}{4}\\,\\frac{{x\\,(\\tan 2x - 2\\tan x)}}{{{{\\sin }^4}x}}\\)\\(= \\mathop {\\lim }\\limits_{x \\to 0} \\frac{1}{4}\\frac{{x\\left\\{ {\\left( {2x +\\frac{1}{3}{{(2x)}^3} + \\frac{2}{{15}}\\,{{(2x)}^5} + ...} \\right) - 2\\left( {x +\\frac{{{x^3}}}{3} + \\frac{2}{{15}}{x^5} + ...} \\right)}\\right\\}}}{{{x^4}\\,{{\\left( {1 - \\frac{{{x^2}}}{{3\\,\\,!}} +\\frac{{{x^4}}}{{5\\,\\,!}} + ....} \\right)}^4}}}\\)   \\(= \\frac{1}{4}\\,.\\,\\left( {\\frac{8}{3} - \\frac{2}{3}} \\right) = \\frac{2}{4} =\\frac{1}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{(1 - \\cos 2x)\\sin5x}}{{{x^2}\\sin 3x}}\\) }is','(a)10/3','(b)3/10','(c)6/5','(d)5/6  ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {{\\rm{lim}}}\\limits_{x \\to 0} \\frac{{(1 - \\cos 2x)\\,\\sin5x}}{{{x^2}\\sin 3x}}\\)\\( = \\mathop {{\\rm{lim}}}\\limits_{x \\to 0} \\frac{{2{{\\sin}^2}x\\,\\sin 5x}}{{{x^2}\\sin 3x}}\\)\\(= \\mathop {{\\rm{lim}}}\\limits_{x \\to 0} \\,\\left( {\\frac{{2{{\\sin}^2}x}}{{{x^2}}}} \\right)\\frac{{\\left( {\\frac{{\\sin 5x}}{x}} \\right)}}{{\\left({\\frac{{\\sin 3x}}{x}} \\right)}}\\)\\( = \\mathop {{\\rm{lim}}}\\limits_{x \\to 0} 2\\,{\\left( {\\frac{{\\sin x}}{x}}\\right)^2} \\times \\frac{{5\\mathop {{\\rm{lim}}}\\limits_{x \\to 0} \\left({\\frac{{\\sin 5x}}{{5x}}} \\right)}}{{3\\mathop {{\\rm{lim}}}\\limits_{x \\to 0} \\left({\\frac{{\\sin 3x}}{{3x}}} \\right)}}\\)\\(= \\frac{{2 \\times 5}}{3} = \\frac{{10}}{3}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{{\\rm{ln}}\\,(\\cos x)}}{{{x^2}}}\\) isequal to ','(a)0','(b)1','(c)\\(\\frac{1}{2}\\)','(d)\\( - \\frac{1}{2}\\)   ','4','FUNCTION, L, C, D','2','Answer =  (d)Applying L-Hospitals rule,  \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\ln (\\cos x)}}{{{x^2}}}\\)\\( = \\mathop{\\lim }\\limits_{x \\to 0} \\frac{{ - \\tan x}}{{2x}}\\)\\(= \\mathop {\\lim }\\limits_{x \\to 0} \\frac{{ - {{\\sec }^2}x}}{2} = \\frac{{ -1}}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' For \\(x \\in R,\\,\\,\\,\\mathop {\\lim }\\limits_{x \\to \\infty } \\,{\\left( {\\frac{{x -3}}{{x + 2}}} \\right)^x}\\) is equal to','(a)e','(b)\\({e^{ - 1}}\\)','(c)\\({e^{ - 5}}\\)','(d)\\({e^5}\\)  ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to \\infty } {\\left( {\\frac{{x + 2 - 5}}{{x + 2}}}\\right)^x} = \\mathop {\\lim }\\limits_{x \\to \\infty } {\\left[ {{{\\left( {1 -\\frac{5}{{x + 2}}} \\right)}^{\\frac{{x + 2}}{{ - 5}}}}} \\right]^{ -\\,\\frac{{5x}}{{x + 2}}}} = {e^{ - 5}}\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\left( {\\frac{{{e^x} - 1}}{x}}\\right)\\) is ','(a)1/2','(b)\\(\\infty \\)','(c)1','(d)0 ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\left( {\\,\\mathop {\\lim }\\limits_{x \\to \\infty } \\frac{{ - 5x}}{{x + 2}} =\\,\\mathop {\\lim }\\limits_{x \\to \\infty } \\frac{{ - 5}}{{1 + \\frac{2}{x}}} =  - 5}\\right).\\)\\(\\mathop {{\\rm{lim}}}\\limits_{x \\to 0} \\frac{{{e^x} - 1}}{x} = \\mathop{{\\rm{lim}}}\\limits_{x \\to 0} \\frac{{\\left( {1 + \\frac{x}{{1!}} +\\frac{{{x^2}}}{{2!}} + ... - 1} \\right)}}{x} = 1\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\left','(a)1','(b)0','(c)\\(\\sqrt a \\)','(d)\\(1/\\sqrt a \\)   ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {{\\rm{lim}}}\\limits_{x \\to 0} \\left[ {\\frac{{\\sqrt {a + x}  - \\sqrt{a - x} }}{x}} \\right]\\)\\(= \\mathop {{\\rm{lim}}}\\limits_{x \\to 0} \\,\\left[ {\\frac{{(\\sqrt {a + x}  -\\sqrt {a - x} )(\\sqrt {a + x}  + \\sqrt {a - x} )}}{{x(\\sqrt {a + x}  + \\sqrt {a -x} )}}} \\right]\\)\\(= \\mathop {{\\rm{lim}}}\\limits_{x \\to 0} \\,\\left[ {\\frac{{2x}}{{x(\\sqrt {a + x} + \\sqrt {a - x} )}}} \\right] = \\frac{2}{{\\sqrt a  + \\sqrt a }} = \\frac{1}{{\\sqrta }}.\\)',CURRENT_TIMESTAMP);
