INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Let \\(f(x) = 4\\) and \\(f(x) = 4\\), then \\(\\mathop {\\lim }\\limits_{x \\to 2}\\,\\frac{{xf(2) - 2f(x)}}{{x - 2}}\\) equals','(a)2','(b)- 2','(c)- 4','(d)3   ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(y = \\mathop {\\lim }\\limits_{x \\to 2} \\frac{{xf(2) - 2f(x)}}{{x - 2}}\\)\\(\\Rightarrow{}\\) \\(y = \\mathop {\\lim }\\limits_{x \\to 2} \\frac{{xf(2) - 2f(2) +2f(2) - 2f(x)}}{{x - 2}}\\)\\(\\Rightarrow{}\\) \\(y = \\mathop {\\lim }\\limits_{x \\to 2} \\frac{{ - 2f(x) + 2f(2)+ xf(2) - 2f(2)}}{{(x - 2)}}\\)\\(\\Rightarrow{}\\) \\(y = \\mathop {\\lim }\\limits_{x \\to 2}  - 2\\frac{{[f(x) -f(2)]}}{{x - 2}} + \\mathop {\\lim }\\limits_{x \\to 2} \\frac{{f(2).(x - 2)}}{{(x -2)}}\\)\\(\\Rightarrow{}\\) \\(y =  - 2\\mathop {\\lim }\\limits_{x \\to 2} \\frac{{f(x) -f(2)}}{{x - 2}} + f(2)\\)\\(\\Rightarrow{}\\) \\(y =  - 2\\,\\,\\mathop {\\lim }\\limits_{x \\to 2} f(x) + f(2) = - \\,8 + 4 =  - \\,4\\).  ',CURRENT_TIMESTAMP);
