INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(1)\\, = 1,\\,f\\,(1)\\, = 2\\), then \\(\\mathop {\\lim }\\limits_{x \\to 1}\\frac{{\\sqrt {f(x)}  - 1}}{{\\sqrt x  - 1}}\\) is','(a)2','(b)4','(c)1','(d)1/2  ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(y = \\mathop {\\lim }\\limits_{x \\to 1} \\frac{{\\sqrt {f(x)}  - 1}}{{\\sqrt x  -1}}\\)\\(\\Rightarrow{}\\) \\(y = \\mathop {\\lim }\\limits_{x \\to 1} \\frac{{\\left( {\\sqrt{f(x)}  - 1} \\right)}}{{\\left( {\\sqrt x  - 1} \\right)}}.\\frac{{\\left( {\\sqrt{f(x)}  + 1} \\right)}}{{\\left( {\\sqrt x  + 1} \\right)}}.\\frac{{\\left( {\\sqrt x  +1} \\right)}}{{\\left( {\\sqrt {f(x)}  + 1} \\right)}}\\)\\(\\Rightarrow{}\\) \\(y = \\mathop {\\lim }\\limits_{x \\to 1} \\frac{{f(x) - 1}}{{x -1}}.\\frac{{\\sqrt x  + 1}}{{\\sqrt {f(x)}  + 1}}\\)\\(\\Rightarrow{}\\) \\(y = \\mathop {\\lim }\\limits_{x \\to 1} \\frac{{f(x) - f(1)}}{{x- 1}}.\\mathop {\\lim }\\limits_{x \\to 1} \\frac{{\\sqrt x  + 1}}{{\\sqrt {f(x)}  +1}}\\)\\(\\Rightarrow{}\\) \\(y = f(1).\\frac{2}{{\\sqrt {f(1)}  + 1}}\\)\\(\\Rightarrow{}\\)\\(y = 2.\\frac{2}{2} = 2\\)Trick : Applying L-Hospitals rule,\\(\\mathop {\\lim }\\limits_{x \\to 1} \\frac{{\\frac{1}{2}{{\\left\\{ {f(x)} \\right\\}}^{- 1/2}}f(x)}}{{\\frac{1}{2}{x^{ - 1/2}}}} = \\mathop {\\lim }\\limits_{x \\to 1}\\frac{{f(x)\\sqrt x }}{{\\sqrt {f(x)} }} = \\frac{{f(1).\\sqrt 1 }}{{\\sqrt {f(1)}}} = 2.\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{n \\to \\infty } \\,{\\left( {\\frac{{{n^2} - n +1}}{{{n^2} - n - 1}}} \\right)^{n(n - 1)}} = \\)','(a)e','(b)\\({e^2}\\)','(c) \\({e^{ - 1}}\\)','(d) 1 ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{n \\to \\infty } {\\left( {\\frac{{{n^2} - n +1}}{{{n^2} - n - 1}}} \\right)^{n(n - 1)}} = \\mathop {\\lim }\\limits_{n \\to \\infty} {\\left( {\\frac{{n(n - 1) + 1}}{{n(n - 1) - 1}}} \\right)^{n(n - 1)}}\\)   \\( = \\mathop {\\lim }\\limits_{n \\to \\infty } \\frac{{{{\\left( {1 + \\frac{1}{{n(n- 1)}}} \\right)}^{n(n - 1)}}}}{{{{\\left( {1 - \\frac{1}{{n(n - 1)}}} \\right)}^{n(n- 1)}}}}\\)\\(= \\frac{e}{{{e^{ - 1}}}} = {e^2}.\\)',CURRENT_TIMESTAMP);
