INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\log (3 + x)\\, - \\log (3 - x)}}{x}= k,\\,\\) then the value of k is','(a)0','(b)\\( - \\frac{1}{3}\\)','(c)\\(\\frac{2}{3}\\)','(d)\\( - \\frac{2}{3}\\) ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\log (3 + x) - \\log (3 - x)}}{x} =k\\)By L-Hos\\(= \\mathop {\\lim }\\limits_{x \\to 7} \\frac{{ - 1}}{{4x\\sqrt {x - 3} }} = \\frac{{- 1}}{{4.7\\sqrt {7 - 3} }} = \\frac{{ - 1}}{{56}}.\\)pitals rule,  \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\frac{1}{{3 + x}} +\\frac{1}{{3 - x}}}}{1} = k\\)  \\(\\Rightarrow{}\\) \\(\\frac{2}{3} = k\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{','(a)0','(b)\\(\\frac{{n + 1}}{n}\\)','(c)n','(d)\\(n + \\frac{1}{n}\\)    ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {\\lim }\\limits_{x \\to 0} n\\frac{{\\sin nx}}{{nx}}.\\mathop {\\lim}\\limits_{x \\to 0} \\left( {(a - n)n - \\frac{{\\tan x}}{x}} \\right) = 0\\)\\(\\Rightarrow{}\\) \\(n((a - n)n - 1) = 0 \\Rightarrow (a - n)n = 1 \\Rightarrow a =n + \\frac{1}{n}\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Given that\\(f\\)(2)=6and \\(f(1) = 4) = \\), then \\(\\mathop {\\lim }\\limits_{h \\to 0} \\frac{{f(2h + 2+ {h^2}) - f(2)}}{{f(h - {h^2} + 1) - f(1)}} = \\) ','(a)Does not exist ','(b)Is equal to - 3/2','(c)Is equal to 3/2','(d)Is equal to 3 ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {\\lim }\\limits_{h \\to 0} \\frac{{f(2h + 2 + {h^2}) - f(2)}}{{f(h -{h^2} + 1) - f(1)}} = \\mathop {\\lim }\\limits_{h \\to 0} \\frac{{f(2h + 2 +{h^2})(2 + 2h)}}{{f(h - {h^2} + 1)(1 - 2h)}}\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\frac{{{e^x} - {e^{ - x}}}}{{\\sin x}}\\) is             ','(a)0','(b)1','(c)2','(d)Non existent   ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(y = \\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{e^x} - {e^{ - x}}}}{{\\sinx}}\\)\\(\\Rightarrow{}\\) \\(y = \\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\left[ {1 +\\frac{x}{{1!}} + \\frac{{{x^2}}}{{2!}} + ....} \\right] - \\left[ {1 -\\frac{x}{{1!}} + \\frac{{{x^2}}}{{2!}} - ....} \\right]}}{{\\sin x}}\\)\\(\\Rightarrow{}\\) \\(y = \\mathop {\\lim }\\limits_{x \\to 0} \\frac{{2\\,\\left[{\\frac{x}{{1!}} + \\frac{{{x^3}}}{{3!}} + \\frac{{{x^5}}}{{5!}} + .............}\\right]}}{{\\sin x}}\\)\\(= \\frac{{6 \\times 2}}{{4 \\times 1}} = 3.\\)\\(\\Rightarrow{}\\) \\(y = \\mathop {\\lim }\\limits_{x \\to 0} \\frac{{2\\,\\left[ {1 +\\frac{{{x^2}}}{{3!}} + \\frac{{{x^4}}}{{4!}} + ...........} \\right]}}{{\\frac{{\\sinx}}{x}}}\\)\\(\\Rightarrow{}\\) \\(y = \\frac{{\\mathop {\\lim }\\limits_{x \\to 0} 2\\,\\left[ {1 +\\frac{{{x^2}}}{{2!}} + .......} \\right]}}{{\\mathop {\\lim }\\limits_{x \\to 0}\\frac{{\\sin x}}{x}}}\\) \\(\\Rightarrow{}\\) \\(y = \\frac{2}{1} = 2\\)Trick : Applying L-Hospitals rule,  \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{e^x} - {e^{ - x}}}}{{\\sin x}}\\)\\(= \\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{e^x} + {e^{ - x}}}}{{\\cos x}} =\\frac{{{e^0} + \\frac{1}{{{e^0}}}}}{{\\cos 0}} = \\frac{{1 + 1}}{1} = 2.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to \\pi /6} \\left','(a)\\(\\sqrt 3 \\)','(b)\\(1/\\sqrt 3 \\)','(c)\\( - \\sqrt 3 \\)','(d)\\( - 1/\\sqrt 3 \\) ','2','FUNCTION, L, C, D','2','Answer =  (b)Using L-Hospitals rule, \\(\\mathop {\\lim }\\limits_{x \\to \\pi /6} \\frac{{3\\cos x + \\sqrt 3 \\sin x}}{6} =\\frac{{3.\\frac{{\\sqrt 3 }}{2} + \\sqrt 3 .\\frac{1}{2}}}{6} = \\frac{1}{{\\sqrt 3 }}.\\)',CURRENT_TIMESTAMP);
