INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{n \\to \\infty } {({3^n} + {4^n})^{\\frac{1}{n}}} =\\)','(a)3','(b)4','(c)\\(\\infty \\)','(d)e  ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {{\\rm{lim}}}\\limits_{n \\to \\infty } \\,{({3^n} +{4^n})^{\\frac{1}{n}}}\\) \\( = \\mathop {{\\rm{lim}}}\\limits_{n \\to \\infty }\\,{({4^n})^{\\frac{1}{n}}}{\\left[ {\\frac{{{3^n}}}{{{4^n}}} + 1}\\right]^{\\frac{1}{n}}}\\)\\( = \\mathop {{\\rm{lim}}}\\limits_{n \\to \\infty } 4\\,{\\left[ {1 +\\frac{1}{{{{\\left( {\\frac{4}{3}} \\right)}^n}}}} \\right]^{1/n}}\\)\\(= 4\\mathop {{\\rm{lim}}}\\limits_{n \\to \\infty } \\,{\\left[ {1 +\\frac{1}{{{{\\left( {\\frac{4}{3}} \\right)}^n}}}} \\right]^{1/n}}\\)    \\( = 4{\\left[ {1 + \\frac{1}{\\infty }} \\right]^0} = 4 \\times {(1)^0}\\)\\(= 4 \\times 1 = 4.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(\\mathop {\\lim }\\limits_{x \\to \\infty } {\\left( {1 + \\frac{a}{x} +\\frac{b}{{{x^2}}}} \\right)^{2x}} = {e^2},\\)then the values of a and b are     ','(a)\\(a = 1,\\;b = 2\\)','(b)\\(\\cos (|x|)\\, - |x|\\)','(c)\\(a \\in R,\\;b = 2\\)','(d)\\(a \\in R,\\;b \\in R\\) ','2','FUNCTION, L, C, D','2','Answer =  (b)Since, \\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\left( {1 + \\frac{a}{x} +\\frac{b}{{{x^2}}}} \\right) = {e^2}\\)\\(\\therefore{}\\) \\(\\mathop {\\lim }\\limits_{x \\to \\infty } {\\left[ {{{\\left( {1 +\\frac{{ax + b}}{{{x^2}}}} \\right)}^{\\frac{{{x^2}}}{{ax + b}}}}}\\right]^{\\frac{{2(ax + b)}}{x}}} = {e^2}\\)\\(\\Rightarrow{}\\) \\(\\mathop {\\lim }\\limits_{x \\to \\infty } {e^{\\frac{{2(ax +b)}}{x}}} = {e^2} \\Rightarrow \\mathop {\\lim }\\limits_{x \\to \\infty } \\frac{{2(ax+ b)}}{x} = 2\\)\\(\\Rightarrow{}\\) \\(2a = 2 \\Rightarrow a = 1\\)Thus \\(a = 1\\) and \\(b \\in R\\). ',CURRENT_TIMESTAMP);
