INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to \\infty } {\\left( {1 - \\frac{4}{{x - 1}}}\\right)^{3x - 1}} = \\)   ','(a)\\({e^{12}}\\)','(b)\\({e^{ - 12}}\\)','(c)\\({e^4}\\)','(d)\\({e^3}\\)   ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to \\infty } {\\left( {1 - \\frac{4}{{x - 1}}}\\right)^{3x - 1}} = \\mathop {\\lim }\\limits_{x \\to \\infty } {\\left[ {{{\\left( {1 +\\frac{{( - 4)}}{{x - 1}}} \\right)}^{\\left( {\\frac{{x - 1}}{{ - 4}}} \\right)}}}\\right]^{\\left( {\\frac{{ - 4}}{{x - 1}}} \\right)(3x - 1)}}\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(\\mathop {\\lim }\\limits_{x \\to 0} \\left ','(a)-1','(b)0','(c)1','(d)None of these   ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\left[ {\\frac{{{e^x} - {e^{\\sin x}}}}{{x -\\sin x}}} \\right]\\),\\(= {e^{\\mathop {\\lim }\\limits_{x \\to \\infty } \\left[ {\\frac{{ - 4\\left( {3 -\\frac{1}{x}} \\right)}}{{\\left( {1 - \\frac{1}{x}} \\right)}}} \\right]}} = {e^{ -12}}.\\)\\(\\left( {\\frac{0}{0}{\\rm{form}}} \\right)\\)Using L-Hospitals rule three times, then\\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{e^x} - {e^{\\sin x}}.\\cos x}}{{1 - \\cosx}} = \\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{e^x} - {e^{\\sin x}}{{\\cos }^2}x +\\sin x.{e^{\\sin x}}}}{{\\sin x}}\\)\\(= \\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{e^x} - {e^{\\sin x}}.{{\\cos }^3}x +{e^{\\sin x}}2\\cos x\\sin x + {e^{\\sin x}}.\\cos x\\sin x + {e^{\\sin x}}.\\cosx}}{{\\cos x}}\\)  \\(= 1.\\)',CURRENT_TIMESTAMP);
