INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{2}{x}\\log (1 + x)\\) isequal to   ','(a)e','(b)\\({e^2}\\)    ','(c) \\(\\frac{1}{2}\\)','(d) 2   ','4','FUNCTION, L, C, D','2','Answer =  (d) \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{2}{x}\\log (1 + x) = \\mathop {\\lim}\\limits_{x \\to 0} 2\\log {(1 + x)^{\\frac{1}{x}}}\\)\\( = \\mathop {\\lim }\\limits_{x \\to 0} 2{\\log _e}e = 2\\)\\(\\left\\{ {\\mathop {\\lim }\\limits_{x \\to 0} {{(1 + x)}^{\\frac{1}{x}}} = {{\\log}_e}e = 1} \\right\\}\\)Tr\\(= \\mathop {\\lim }\\limits_{x \\to  - 1} \\frac{{(x + 1)(x + 2)}}{{(x + 1)(x + 3)}}= \\mathop {\\lim }\\limits_{x \\to  - 1} \\frac{{x + 2}}{{x + 3}} = \\frac{1}{2}.\\)ick : Using L Hospitals rule. ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(\\mathop {\\lim }\\limits_{x \\to \\infty } {\\left( {\\frac{{3x -4}}{{3x + 2}}} \\right)^{\\frac{{x + 1}}{3}}}\\) is equal to ','(a)\\({e^{ - 1/3}}\\)','(b)\\({e^{ - 2/3}}\\)','(c)\\({e^{ - 1}}\\)','(d)\\({e^{ - 2}}\\)    ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to \\infty } {\\left( {\\frac{{3x - 4}}{{3x + 2}}}\\right)^{\\frac{{x + 1}}{3}}} = \\mathop {\\lim }\\limits_{x \\to \\infty } {\\left({\\frac{{3x + 2 - 6}}{{3x + 2}}} \\right)^{\\frac{{x + 1}}{3}}}\\)\\(= \\mathop {\\lim }\\limits_{x \\to \\infty } {\\left( {1 - \\frac{6}{{3x + 2}}}\\right)^{\\frac{{x + 1}}{3}}} = \\mathop {\\lim }\\limits_{x \\to \\infty } {\\left[{{{\\left( {1 - \\frac{6}{{3x + 2}}} \\right)}^{\\frac{{3x + 2}}{{ - 6}}}}}\\right]^{\\frac{{ - 6}}{{3x + 2}}.\\frac{{x + 1}}{3}}}\\)\\( = \\mathop {\\lim }\\limits_{x \\to \\infty } {e^{\\frac{{ - 2(x + 1)}}{{3x + 2}}}}= {e^{ - 2/3}}\\), \\(\\left\\{ {\\mathop {\\lim }\\limits_{x \\to \\infty } \\frac{{ - 2(x+ 1)}}{{3x + 2}} = \\frac{{ - 2}}{3}} \\right\\}\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value  of \\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\frac{{(x + 1)(3x +4)}}{{{x^2}(x - 8)}}\\) is equal to   ','(a)2','(b)3','(c)1','(d)0    ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\frac{{(x + 1)(3x + 4)}}{{{x^2}(x -8)}} = \\mathop {\\lim }\\limits_{x \\to \\infty } \\left[ {\\frac{{x\\left( {1 +\\frac{1}{x}} \\right)\\,x\\,\\left( {3 + \\frac{4}{x}} \\right)}}{{{x^3}\\left( {1 -\\frac{8}{x}} \\right)}}} \\right]\\)\\(= \\mathop {\\lim }\\limits_{x \\to \\infty } \\left[ {\\frac{1}{x}\\frac{{\\left( {1 +\\frac{1}{x}} \\right)\\,\\left( {3 + \\frac{4}{x}} \\right)}}{{\\left( {1 -\\frac{8}{x}} \\right)}}} \\right] = 0.\\)  ',CURRENT_TIMESTAMP);
