INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of the constant \\(\\alpha \\) and \\(\\beta \\) such that \\(\\mathop {\\lim}\\limits_{x \\to \\infty } \\left( {\\frac{{{x^2} + 1}}{{x + 1}} - \\alpha x - \\beta }\\right) = 0\\) are respectively','(a)(1, 1)','(b)(-1, 1)','(c)(1, -1)','(d)(0, 1)  ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\left( {\\frac{{{x^2} + 1}}{{x + 1}} -2x - \\beta } \\right) = 0\\)\\(\\Rightarrow{}\\) \\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\frac{{{x^2}(1 -\\alpha ) - x(\\alpha  + \\beta ) + 1 - b}}{{x + 1}} = 0\\)Since the limit of the given expression is zero, therefore degree of thepolynomial in numerator must be less than denominator.\\(\\therefore{}\\) \\(1 - \\alpha  = 0\\) and \\(\\alpha  + \\beta  = 0\\)\\(\\Rightarrow{}\\) \\(\\alpha  = 1\\) and \\(\\beta  =  - 1\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Let \\(f:R \\to R\\)be a differentiable function having \\(f(2) = 6,f(2) = \\left({\\frac{1}{{48}}} \\right).\\) Then \\(\\mathop {\\lim }\\limits_{x \\to 2}\\int\\limits_6^{f(x)} {\\frac{{4{t^3}}}{{x - 2}}} \\)dt equals','(a)12','(b)18','(c)24','(d)36   ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to 2} \\frac{{\\int\\limits_6^{f(x)} {4{t^3}dt}}}{{x - 2}}\\,\\,(0/0\\,{\\rm{form}}) = \\mathop {\\lim }\\limits_{x \\to 2}\\frac{{4{{(f(x))}^3} \\times f(x)}}{1}\\) \\(= 4{(f(2))^3} \\times f(2) = 18.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' \\(','(a)0','(b)\\( - \\frac{1}{2}\\)','(c)\\(\\log \\left( {\\frac{2}{3}} \\right)\\)','(d)None of these  ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{n \\to \\infty } \\,\\left[ {\\frac{1}{{1 - {n^2}}} +\\frac{2}{{1 - {n^2}}} + ..... + \\frac{n}{{1 - {n^2}}}} \\right]\\) \\(= \\mathop {\\lim }\\limits_{n \\to \\infty } \\,\\frac{{\\Sigma n}}{{1 - {n^2}}} =\\frac{1}{2}\\,\\,\\mathop {\\lim }\\limits_{n \\to \\infty } \\,\\,\\,\\frac{{{n^2} + n}}{{1- {n^2}}} =  - \\frac{1}{2}.\\)',CURRENT_TIMESTAMP);
