INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If\\(f(x) = \\left\\{ \\begin{array}{l}\\frac{{|x - a|}}{{x - a}},{\\rm{when}}\\,x \\ne a\\\\\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,1,{\\rm{when}}\\,x = a\\end{array} \\right.\\),then ','(a)\\(f(x)\\)is continuous at \\(x = a\\)    ','(b)\\(f(x)\\)is discontinuous at \\(x = a\\)','(c)\\(\\mathop {\\lim }\\limits_{x \\to a} f(x) = 1\\)','(d)None of these ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to a - } f(x) =  - 1,\\,\\mathop {\\lim }\\limits_{x\\to a + } f(x) = 1,\\,\\,f(a) = 1.\\)    ',CURRENT_TIMESTAMP);
