INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ \\begin{array}{l}1 + x,\\;{\\rm{when }}x \\le 2\\\\5 - x,\\,{\\rm{when }}\\,x \\le 3\\end{array} \\right.\\), then  ','(a)\\(f(x)\\)is continuous at \\(x = 2\\)    ','(b)\\(f(x)\\)is discontinuous at \\(A = 0,\\,B = 1\\)','(c)\\(f(x)\\)is continuous at\\(x = 3\\)','(d)None of these  ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to 2 - } f(x) = 3,\\,\\mathop {\\lim }\\limits_{x\\to 2 + } \\,f(x) = 3\\) and \\(\\mathop {\\lim }\\limits_{x \\to {0^ - }} f(x) =\\mathop {\\lim }\\limits_{h \\to 0} f(0 - h) = 0\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If\\(f(x) = \\left\\{ \\begin{array}{l}\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,1,\\,{\\rm{when}}\\,\\,0 < x \\le \\frac{{3\\pi }}{4}\\\\2\\sin \\frac{2}{9}x,{\\rm{when}}\\,\\frac{{3\\pi }}{4} < x < \\pi\\end{array} \\right.\\), then ','(a)\\(f(x)\\)is continuous at \\(x = 0\\)    ','(b)\\(f(x)\\)is continuous at \\(x = \\pi \\)','(c)\\(f(x)\\)is continuous at \\(x = \\frac{{3\\pi }}{4}\\)','(d)\\(f(x)\\)is discontinuous at \\(x = \\frac{{3\\pi }}{4}\\) ','3','FUNCTION, L, C, D','2','Answer =  (c)Here \\(f\\,\\left( {\\frac{{3\\pi }}{4}} \\right) = 1\\) and \\(f\\)\\(\\mathop {\\lim }\\limits_{x \\to 3\\pi /4 + } f(x) = \\mathop {\\lim }\\limits_{h \\to0} \\,\\,2\\sin \\frac{2}{9}\\,\\left( {\\frac{{3\\pi }}{4} + h} \\right) = 2\\,\\sin\\frac{\\pi }{6} = 1.\\)Hence \\(f(x)\\) is continuous at \\(\\frac{{\\sin \\,\\,2x}}{{\\sin \\,\\left({\\frac{x}{2}} \\right)}}\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ \\begin{array}{l}\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,x\\sin x,\\,{\\rm{when }}0 < x \\le \\frac{\\pi }{2}\\\\\\frac{\\pi }{2}\\sin (\\pi  + x),{\\rm{when}}\\frac{\\pi }{{\\rm{2}}} < x < \\pi\\end{array} \\right.\\), then    ','(a)\\(f(x)\\)is discontinuous at \\(x = \\pi /2\\)   ','(b)\\(f(x)\\)is continuous at \\(x = \\pi /2\\)','(c)\\(f(x)\\)is continuous at \\(x = 0\\)','(d)None of these   ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to \\infty } \\,\\frac{{4x}}{{(\\sqrt {{x^2} + 8x +3}  + \\sqrt {{x^2} + 4x + 3} }}\\) and \\(f\\left( {\\frac{\\pi }{2}} \\right) =\\frac{\\pi }{2}.\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ \\begin{array}{l}\\,\\,\\,\\,\\,\\,\\,\\,\\,\\frac{{1 - \\cos 4x}}{{{x^2}}},\\;\\;{\\rm{when}}\\,x < 0\\\\\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,a,\\,\\,\\,{\\rm{when}}\\,\\,x= 0\\\\\\frac{{\\sqrt x }}{{\\sqrt {(16 + \\sqrt x )}  - 4}},\\,\\,{\\rm{when}}\\,x > 0\\end{array} \\right.\\),is continuous at \\(x = 0\\), then the value of a will be    ','(a)8','(b)-8','(c)4','(d)None of these   ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to 0 - } f(x) = \\mathop {\\lim }\\limits_{x \\to 0- } \\left( {\\frac{{2\\,{{\\sin }^2}2x}}{{{{(2x)}^2}}}} \\right)\\,4 = 8\\)\\(\\mathop {\\lim }\\limits_{x \\to 0 + } f(x) = \\mathop {\\lim }\\limits_{x \\to 0 + }\\sqrt {16 + \\sqrt x  + 4}  = 8\\). Hence \\(a = 8\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ \\begin{array}{l}a{x^2} - b,\\,\\,{\\rm{when }}0 \\le x < 1\\\\\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,2,{\\rm{when }}x = 1\\\\\\,\\,\\,\\,x + 1,\\,\\,{\\rm{when1}} < x \\le 2\\end{array} \\right.\\)is continuous at \\(x = 1\\), then the most suitable value ofa, b are    ','(a)\\(a = 2,\\;b = 0\\)','(b)\\(a = 1,\\;b =  - 1\\)','(c)\\(a = 4,\\;b = 2\\)','(d)All the above    ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {\\lim }\\limits_{x \\to 1 - } f(x) = a - b,\\,\\,\\mathop {\\lim}\\limits_{x \\to 1 + } f(x) = 2\\,\\, \\Rightarrow a - b = 2\\)All the given sets of a, b make \\(f(x)\\) continuous at x=1.  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ \\begin{array}{l}\\frac{{x - |x|}}{x},{\\rm{when}}\\,x \\ne 0\\\\\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,2,\\,{\\rm{when}}\\,x = 0\\end{array} \\right.\\), then ','(a) f(x)is continuous at \\(x = 0\\) ','(b) f(x) is discontinuous at \\(x = 0\\)','(c)\\(\\mathop {\\lim }\\limits_{x \\to 0} f(x) = 2\\)','(d)None of these ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to 0 - } f(x) = 1 + 1 = 2,\\,\\,\\mathop {\\lim}\\limits_{x \\to 0 + } f(x) = 0,\\,\\,f(0) = 2\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ \\begin{array}{l}\\frac{{{x^4} - 16}}{{x - 2}},\\,\\,{\\rm{when}}\\,x \\ne 2\\\\\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,16,\\,{\\rm{when}}\\,x = 2\\end{array} \\right.\\), then ','(a)\\(f(x)\\)is continuous at \\(x = 2\\)    ','(b)\\(f(x)\\)is discountinous at \\(x = 2\\) ','(c)\\(\\mathop {\\lim }\\limits_{x \\to 2} f(x) = 16\\)  ','(d)None of these ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to 2} \\,f(x) = \\mathop {\\lim }\\limits_{x \\to 2}\\,(x + 2)\\,\\,({x^2} + 4) = 32,\\,\\,f(2) = 16.\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ \\begin{array}{l}\\,\\,\\,\\,{x^2},\\,{\\rm{when}}\\,x \\le 1\\\\x + 5,{\\rm{when }}x > {\\rm{1}}\\end{array} \\right.\\), then ','(a)\\(f(x)\\)is continuous at \\(x = 1\\)    ','(b)\\(f(x)\\)is discontinuous at\\(x = 1\\)','(c)\\(\\mathop {\\lim }\\limits_{x \\to 1} f(x) = 1\\)','(d)None of these  ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to 1 - } f(x) = 1,\\mathop {\\lim }\\limits_{x \\to1 + } f(x) = 6.\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ \\begin{array}{l}\\frac{{{x^2} + 3x - 10}}{{{x^2} + 2x - 15}},\\;\\;{\\rm{when }}x \\ne  - 5\\\\\\,\\,a\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,,{\\rm{when }}x =  - 5\\end{array} \\right.\\)is continuous at \\(x =  - 5\\), then the value of a willbe   ','(a)\\(\\frac{3}{2}\\)','(b)\\(\\frac{7}{8}\\)','(c)\\(\\frac{8}{7}\\)','(d)\\(\\frac{2}{3}\\)    ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to \\, - \\,5} f(x) = \\frac{{(x - 2)\\,\\,(x +5)}}{{(x + 5)\\,(x - 3)}} = \\frac{{ - 7}}{{ - 8}} = \\frac{7}{8}.\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ \\begin{array}{l}x + \\lambda ,\\;x\\, < 3\\\\\\,\\,\\,\\,\\,\\,\\,\\,\\,4,\\,\\,x = 3\\\\3x - 5,\\,\\,x > 3\\end{array} \\right.\\)is continuous at\\(x = 3\\), then \\(\\lambda  = \\)    ','(a)4','(b)3','(c)2','(d)1    ','4','FUNCTION, L, C, D','2','Answer =  (d)By definition of continuity, we know that\\(\\mathop {\\lim }\\limits_{x \\to 3 + } f(x) = f(3) = \\mathop {\\lim }\\limits_{x \\to3 - } f(x)\\)\\( \\Rightarrow \\,\\mathop {\\lim }\\limits_{x \\to 3 - } f(x) = 4\\) or \\(\\mathop{\\lim }\\limits_{h \\to 0} 3 - h + \\lambda  = 4\\)\\(f(3) = 3.7\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of k which makes \\(f(x) = \\left\\{ \\begin{array}{l}\\sin \\frac{1}{x},\\;x \\ne 0\\\\\\,\\,\\,\\,\\,\\,\\,\\,k,\\,x = 0\\end{array} \\right.\\) continuous at \\(x = 0\\)is   ','(a)8','(b)1','(c)-1','(d)None of these   ','4','FUNCTION, L, C, D','2','Answer =  (d)If \\(x \\to 0,\\) then the value of \\(\\sin \\frac{1}{x}\\) passes through[-1, 1] infinitely many ways, therefore limit of the function does not exist at \\(x =0.\\) Hence there is no value of k for which the function is continuous at \\(x =0.\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ \\begin{array}{l}\\sin x,\\;x \\ne n\\pi ,\\;\\;n \\in Z\\\\\\,\\,\\,\\,\\,\\,2,\\,{\\rm{otherwise}}\\end{array} \\right.\\) and \\(g(x) = \\left\\{ \\begin{array}{l}{x^2} + 1,\\;x \\ne 0,\\,2\\\\\\,\\,\\,\\,\\,\\,\\,\\,\\,4,\\,x = 0\\\\\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,5,x = 2\\end{array} \\right.,\\)  then \\(\\mathop {\\lim }\\limits_{x \\to 0} g\\,\\{ f(x)\\} \\)is ','(a)5','(b)6','(c)7','(d)1    ','4','FUNCTION, L, C, D','2','Answer =  (d)As we are given \\(f(x) = \\sin x\\), if \\(x \\ne n\\pi \\)i.e., \\(x \\ne 0,\\,\\pi ,\\,\\,2\\pi ,....\\)= 2 otherwise\\(\\therefore \\,\\,\\,\\mathop {\\lim }\\limits_{x \\to {0^ + }} g\\,\\{ f(x)\\}  =\\,\\mathop {\\lim }\\limits_{x \\to {0^ + }} g\\,\\{ \\sin x\\}  = \\,\\mathop {\\lim}\\limits_{x \\to {0^ + }} ({\\sin ^2}x + 1)=1 \\)Similarly, \\(\\,\\mathop {\\lim }\\limits_{x \\to {0^ - }} g\\,\\left\\{ {f(x)} \\right\\}= 1.\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Let \\(f(x) = \\left\\{ \\begin{array}{l}\\frac{{x - 4}}{{|x - 4|}} + a,\\;x < 4\\\\\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,a + b,\\,x = 4\\\\\\frac{{x - 4}}{{|x - 4|}} + b,\\,x > 4\\end{array} \\right.\\). Then \\(f(x)\\)is continuous at \\(x = 4\\) when    ','(a)\\(a = 0,\\;b = 0\\)','(b)\\(a = 1,\\;b = 1\\)','(c)\\(a =  - 1,\\;b = 1\\)','(d)\\(a = 1,\\;b =  - 1\\)  ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(\\mathop {\\lim }\\limits_{x \\to 4 - } f(x) = \\mathop {\\lim }\\limits_{h \\to 0}f(4 - h) = \\mathop {\\lim }\\limits_{h \\to 0} \\,\\,\\frac{{4 - h - 4}}{{|4 - h - 4|}}+ a\\)\\(= \\mathop {\\lim }\\limits_{h \\to 0}  - \\frac{h}{h} + a = a - 1.\\)\\(= \\mathop {\\lim }\\limits_{x \\to 4 + } f(x) = \\mathop {\\lim }\\limits_{h \\to 0}\\,\\,f(4 + h) = \\mathop {\\lim }\\limits_{h \\to 0} \\,\\frac{{4 + h - 4}}{{|4 + h -4|}} + b = b + 1\\)and \\(f(4) = a + b\\)Since \\(f(x)\\) is continuous at \\(x = 4\\)Therefore \\(\\mathop {\\lim }\\limits_{x \\to 4 - } f(x) = f(4) = \\mathop {\\lim}\\limits_{x \\to 4 + } f(x)\\)\\( \\Rightarrow \\,\\,a - 1 = a + b = b + 1\\,\\, \\Rightarrow \\,\\,b =  - 1\\) and \\(a= 1.\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Let \\(f(x) = \\left\\{ \\begin{array}{l}\\frac{{{x^4} - 5{x^2} + 4}}{{|(x - 1)(x - 2)|}},\\;\\;x \\ne 1,\\;2\\\\\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,6,\\,\\,\\,x = 1\\\\\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,12,\\,\\,\\,x = 2\\end{array} \\right.\\)Then \\(f(x)\\)is continuous on the set ','(a)R','(b)\\(R - \\{ 1\\} \\)','(c)\\(R - \\{ 2\\} \\)','(d)\\(f:R \\to R\\) ','4','FUNCTION, L, C, D','2','Answer =  (d)For any \\(x \\ne 1,\\,\\,2\\)we find that \\(f(x)\\) is the quotient of twopolynomials and a polynomial is everywhere continuous. Therefore \\(f(x)\\) iscontinuous for all \\(x \\ne 1,\\,\\,2.\\) Check continuity at \\(x = 1,\\,\\,2.\\)   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The value of \\(f(0)\\), so that the function \\(f(x) = \\frac{{{{(27 - 2x)}^{1/3}}- 3}}{{9 - 3{{(+ 5x)}^{1/5}}}},\\,(x \\ne 0)\\)is continuous, is given by','(a) 2/3','(b) 6','(c)2','(d)4   ','3','FUNCTION, L, C, D','2','Answer =  (c)Since \\(f(x)\\) is continuous at \\(x = 0,\\) therefore\\(f(0) = \\mathop {\\lim }\\limits_{x \\to 0} \\,f(x) = \\mathop {\\lim }\\limits_{x \\to0} \\,\\frac{{{{(27 - 2x)}^{1/3}} - 3}}{{9 - 3\\,{{(Answer = + 5x)}^{1/5}}}}\\,\\)\\(\\left( {{\\rm{Form}}\\frac{{\\rm{0}}}{{\\rm{0}}}} \\right),\\)\\(= \\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{{\\frac{1}{3}\\,{{(27 - 2x)}^{ -2/3}}( - 2)}}{{ - \\frac{3}{5}\\,{{(Answer = + 5x)}^{ - 4/5}}(5)}} = 2.\\) ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the function \\(f(x) = \\left\\{ \\begin{array}{l}{(\\cos x)^{1/x}},\\;x \\ne 0\\\\\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,k,\\,x = 0\\end{array} \\right.\\)is continuous at \\(x = 0\\), then the value of kis   ','(a)1','(b)-1','(c)0','(d)e   ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,{(\\cos x)^{1/x}} = k\\,\\, \\Rightarrow\\,\\,\\mathop {\\lim }\\limits_{x \\to 0} \\frac{1}{x}\\log \\,(\\cos x) = \\log k\\)\\(\\Rightarrow \\,\\,\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{1}{x}\\,\\,\\mathop{\\lim }\\limits_{x \\to 0} \\,\\,\\log \\,\\cos x = \\log k\\)  \\(\\Rightarrow \\,\\,\\,\\mathop {\\lim }\\limits_{x \\to 0} \\,\\,\\frac{1}{x} \\times 0 ={\\log _e}k\\,\\, \\Rightarrow \\,k = 1.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Function \\(f(x) = \\left\\{ \\begin{array}{l}\\,\\,\\,x - 1,\\;x < 2\\\\2x - 3,\\,x \\ge 2\\end{array} \\right.\\)is  a continuous function   ','(a)For all real values of x  ','(b)For \\(x = 2\\)only','(c)For all real values of x such that \\(x \\ne 2\\)','(d)For all integral values of x only ','1','FUNCTION, L, C, D','2','Answer =  (a)Since \\(\\mathop {\\lim }\\limits_{x \\to 2 - } f(x) = \\mathop {\\lim }\\limits_{x\\to 2 + } f(x) = f(2) = 1\\)Also it is continuous for all values of x, less than 2 and greater than 2. ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the function \\(f(x) = \\left\\{ \\begin{array}{l}1 + \\sin \\frac{{\\pi x}}{2}\\,\\,,\\,{\\rm{for}}\\, - \\infty  < x \\le 1\\\\\\,\\,\\,\\,\\,\\,\\,\\,ax + b,\\,{\\rm{for}}\\,1 < x < 3\\\\\\,\\,\\,\\,6\\tan \\frac{{x\\pi }}{{12}},\\,{\\rm{for}}3 \\le x < 6\\end{array} \\right.\\) is continuous in the interval \\(( - \\infty ,\\,6)\\), thenthe values of a and b are respectively   ','(a)0, 2','(b)1, 1','(c)2, 0','(d)2, 1    ','3','FUNCTION, L, C, D','2','Answer =  (c)Given function is continuous at all point in \\(( - \\,\\infty ,\\,\\,6)\\) and at\\(x = 1,\\,\\,x = 3\\) function is continuous.If function \\(f(x)\\) is continuous at \\(x = 1,\\) then\\(\\mathop {\\lim }\\limits_{x \\to {1^ - }} \\,f(x) = \\mathop {\\lim }\\limits_{x \\to{1^ + }} \\,f(x)\\)\\(\\Rightarrow \\,\\,\\,1 + \\sin \\frac{\\pi }{2} = a + b\\)\\(\\therefore \\,\\,\\,a + b = 2\\).....(i)If at \\(x = 3,\\) function is continuous, then\\(\\mathop {\\lim }\\limits_{x \\to {3^ - }} \\,f(3) = \\mathop {\\lim }\\limits_{x \\to{3^ + }} \\,f(x)\\)\\(\\Rightarrow \\,\\,3a + b = 6\\tan \\frac{{3\\pi }}{{12}}\\)\\(\\therefore \\,\\,\\,3a + b = 6\\).....(ii)From (i) and (ii), \\(a = 2,\\,\\,b = 0\\) .    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ \\begin{array}{l}x\\sin \\frac{1}{x},\\,\\,x \\ne 0\\\\\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,k,\\,\\,x = 0\\end{array} \\right.\\)is continuous at \\(x = 0\\), then the value of k is    ','(a)1','(b)-1','(c)0','(d)2   ','3','FUNCTION, L, C, D','2','Answer =  (c)If function \\(f(x)\\) is continuous at \\(x = 0,\\) then\\(f(0) = \\mathop {\\lim }\\limits_{x \\to 0} \\,f(x)\\)Given \\(f(0) = k\\);  \\(f(0) = k = \\mathop {\\lim }\\limits_{x \\to 0} \\,x\\,\\left({\\sin \\frac{1}{x}} \\right)\\)\\(f(0) = k = 0,{\\rm{  }}\\left( { - 1 \\le \\sin \\frac{1}{x} \\le 1} \\right)\\); \\(\\therefore \\,\\,\\,k = 0\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ \\begin{array}{l}\\,\\,\\,\\,\\,\\,\\,\\frac{{\\sin     ','(a)Equal to 0','(b)Equal to 1','(c)Equal to -1','(d)Indeterminate    ','1','FUNCTION, L, C, D','2','Answer =  (a)If f is continuous at \\(\\mathop {\\lim }\\limits_{x \\to {0^ + }} f(x) =\\mathop {\\lim }\\limits_{h \\to 0} f(0 + h) = \\mathop {\\lim }\\limits_{h \\to 0} 2(0+ h) = 0\\), then\\(\\mathop {\\lim }\\limits_{x \\to {0^ - }} f(x) = \\mathop {\\lim }\\limits_{x \\to{0^ + }} f(x) = f(0)\\)\\(\\Rightarrow \\,f(0) = \\,\\mathop {\\lim }\\limits_{x \\to {0^ - }} f(x)\\)\\(k = \\mathop {\\lim }\\limits_{h \\to 0} f(0 - h) = \\mathop {\\lim }\\limits_{h \\to 0}\\,\\frac{{\\cos \\frac{\\pi }{2}\\,[0 - h]}}{{[0 - h]}}\\)\\(k = \\mathop {\\lim }\\limits_{h \\to 0} \\,\\frac{{\\cos \\frac{\\pi }{2}\\,[ - h]}}{{[ -h]}} = \\mathop {\\lim }\\limits_{h \\to 0} \\,\\frac{{\\cos \\frac{\\pi }{2}\\,[ - h -1]}}{{[ - h - 1]}}\\)\\(k = \\mathop {\\lim }\\limits_{h \\to 0} \\,\\frac{{\\cos \\,\\left( { - \\frac{\\pi}{2}} \\right)}}{{ - 1}}\\); \\(k = 0\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(f(x)\\, = \\left\\{ \\begin{array}{l}x + 2\\,\\,\\,\\,,\\,\\,\\,1 \\le x \\le 2\\\\4\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,,\\,\\,\\,x = 2\\\\3x - 2\\,\\,,\\,\\,\\,x > 2\\end{array} \\right.\\) is continuous at','(a)\\(x = 2\\) only','(b)\\(x \\le 2\\)','(c)\\(x \\ge 2\\)','(d)None of these  ','3','FUNCTION, L, C, D','2','Answer =  (c)Clearly the function is defined only in the interval \\([1,\\,\\infty )\\) henceoption (b) cannot even apply. For \\(x > 2,\\,y = 3x - 2\\) which is a straightline, hence continuous. Further \\(y = 4\\) at \\(x = 2\\). Hence, the function iscontinuous at \\(x = 2\\) also (but not at \\(x = 2\\) only).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the function \\(f(x) = \\,\\left\\{ {\\begin{array}{*{20}{c}}{5x - 4}&,&{{\\rm{if}}}&{0 < x \\le 1}\\\\{4{x^2} + 3bx}&,&{{\\rm{if}}}&{1 < x < 2}\\end{array}} \\right.\\) is continuous at every point of its domain, then thevalue of b is','(a)- 1','(b)0','(c)1','(d)None of these   ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(f(x)\\) is continuous at every point of its domain,\\(\\Rightarrow{}\\)\\(\\mathop {\\lim }\\limits_{x \\to {1^ - }} f(x) = \\mathop {\\lim}\\limits_{x \\to {1^ + }} f(x) = f(1)\\)\\(\\Rightarrow{}\\) \\(5 \\times 1 - 4 = 4 \\times 1 + 3 \\times b \\times 1\\)\\(\\Rightarrow{}\\) \\(1 = 4 + 3b\\)  \\(\\Rightarrow{}\\)  \\(3b =  - 3\\)\\(\\Rightarrow{}\\) \\(b =  - 1\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The values of A and B such that the function \\(f(x) = \\left\\{{\\begin{array}{*{20}{c}}{ - 2\\sin x,}&{x \\le  - \\frac{\\pi }{2}}\\\\{A\\sin x + B,}&{ - \\frac{\\pi }{2} < x < \\frac{\\pi }{2}}\\\\{\\cos x,}&{x \\ge \\frac{\\pi }{2}}\\end{array}} \\right.\\), is continuous everywhere are  ','(a)\\(A = 0,\\,B = 1\\)','(b)\\(A = 1,\\,B = 1\\)','(c)\\(A =  - 1,\\,B = 1\\)','(d)\\(A =  - 1,\\,B = 0\\)    ','3','FUNCTION, L, C, D','2','Answer =  (c)For continuity at all \\(x \\in R,\\) we must have \\(f\\left( { - \\frac{\\pi }{2}}\\right) = \\mathop {{\\rm{lim}}}\\limits_{x \\to {{( - \\pi /2)}^ - }} ( - 2\\sinx)\\)\\( = \\mathop {{\\rm{lim}}}\\limits_{x \\to {{( - \\pi /2)}^ + }} (A\\sin x + B)\\)\\(\\Rightarrow{}\\) \\(2 =  - A + B\\)  .....(i)and \\(f\\left( {\\frac{\\pi }{2}} \\right) = \\mathop {\\lim }\\limits_{x \\to {{(\\pi/2)}^ - }} (A\\sin x + B)\\)\\( = \\mathop {{\\rm{lim}}}\\limits_{x \\to {{(\\pi /2)}^ +}} (\\cos x)\\)\\(\\Rightarrow{}\\) \\(0 = A + B\\).....(ii)From (i) and (ii), \\(A =  - 1\\) and \\(B = 1\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\frac{{{x^2} - 10x + 25}}{{{x^2} - 7x + 10}}\\) for \\(x\\)\\( \\ne\\,\\)5and f is continuous at \\(x = 5,\\) then \\(f(5) = \\)','(a)0','(b)5','(c)10','(d)25   ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(f(5) = \\mathop {{\\rm{lim}}}\\limits_{x \\to 5} f(x)\\)\\( = \\mathop{{\\rm{lim}}}\\limits_{x \\to 5} \\frac{{{x^2} - 10x + 25}}{{{x^2} - 7x + 10}}\\)  \\(= \\mathop {{\\rm{lim}}}\\limits_{x \\to 5} \\frac{{{{(x - 5)}^2}}}{{(x - 2)(x -5)}} = \\frac{{5 - 5}}{{5 - 2}} = 0.\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' In order that the function \\(f(x) = {(x + 1)^{\\cot \\,x}}\\) is continuous at \\(x= 0\\), \\(f(0)\\) must be defined as','(a)\\(f(0) = \\frac{1}{e}\\)','(b)\\(f(0) = 0\\)','(c)\\(f(0) = e\\)','(d)None of these    ','3','FUNCTION, L, C, D','2','Answer =  (c)For continuity at \\(x = 0\\), we must have \\(f(0) = \\mathop {{\\rm{lim}}}\\limits_{x\\to 0} f(x)\\)\\(\\mathop {\\lim }\\limits_{x \\to \\alpha } \\,\\frac{{1 - \\cos \\,(a{x^2}+ bx + c)}}{{{{(x - \\alpha )}^2}}}\\)\\( = \\mathop {{\\rm{lim}}}\\limits_{x \\to 0}{\\left\\{ {{{(1 + x)}^{\\frac{1}{x}}}} \\right\\}^{x\\cot x}}\\)  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(f(x) = \\sin |x|\\) is ','(a)Continuous for all x','(b)Continuous only at certain points','(c)Differentiable at all points','(d)None of these  ','1','FUNCTION, L, C, D','2','Answer =  (a)It\\( = \\mathop {{\\rm{lim}}}\\limits_{x \\to 0} {\\left\\{ {{{(1 +x)}^{\\frac{1}{x}}}} \\right\\}^{\\mathop {{\\rm{lim}}}\\limits_{x \\to 0} \\,\\left({\\frac{x}{{\\tan x}}} \\right)}}\\)\\(= {e^1} = e.\\)is obvious.  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\,|x|\\), then \\(f(x)\\) is ','(a)Continuous for all x','(b)Differentiable at \\(x = 0\\)','(c)Neither continuous nor differentiable at \\(x = 0\\)','(d)None of these  ','1','FUNCTION, L, C, D','2','Answer =  (a)It is obvious that \\(|x|\\) is continuous for all x.Now, \\(Rf(x) = \\mathop {\\lim }\\limits_{h \\to 0} \\frac{{|0 + h| - 0}}{h} = 1\\)\\(Lf(x) = \\mathop {\\lim }\\limits_{h \\to 0} \\frac{{|0 - h| - 0}}{{ - h}} =  - 1\\)Hence \\(f(x) = \\,|x|\\) is not differentiable at \\(x = 0\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ {\\begin{array}{*{20}{c}}{\\frac{{1 - \\sin x}}{{\\pi  - 2x}},}&{x \\ne \\frac{\\pi }{2}}\\\\{\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\lambda \\,,}&{x = \\frac{\\pi }{2}}\\end{array}} \\right.\\), be continuous at \\(x = \\pi /2,\\) then value of \\(\\lambda\\) is','(a)-1','(b)1','(c)0','(d)2   ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(f(x)\\) is continuous at \\(x = \\frac{\\pi }{2}\\), then\\(\\mathop {\\lim }\\limits_{x \\to \\pi /2} f(x) = f(0)\\) or \\(\\lambda  = \\mathop{\\lim }\\limits_{x \\to \\pi /2} \\frac{{1 - \\sin x}}{{\\pi  - 2x}}\\),  \\(\\left({\\frac{0}{0}{\\rm{ form}}} \\right)\\)Applying L-Hospitals rule,\\(\\lambda  = \\mathop {\\lim }\\limits_{x \\to \\pi /2} \\frac{{ - \\cos x}}{{ - 2}}\\)\\(\\Rightarrow{}\\) \\(\\lambda  = \\mathop {\\lim }\\limits_{x \\to \\pi /2} \\frac{{\\cosx}}{2} = 0.\\)    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Let \\(f(x) = \\,\\left\\{ {\\begin{array}{*{20}{c}}{\\frac{{\\sin \\pi x}}{{5x}},}&{x \\ne 0}\\\\{k,}&{x = 0}\\end{array}} \\right.\\). If \\(f(x)\\) is continuous at \\(x = 0,\\) then \\(k =\\)','(a)\\(\\frac{\\pi }{5}\\)','(b)\\(\\frac{5}{\\pi }\\)','(c)1','(d)0  ','1','FUNCTION, L, C, D','2','Answer =  (a)Since \\(f(x)\\) is continuous at \\(x = 0,\\) therefore \\(\\mathop{{\\rm{lim}}}\\limits_{x \\to 0} f(x) = f(0)\\) \\(\\Rightarrow{}\\) \\(\\mathop{{\\rm{lim}}}\\limits_{x \\to 0} \\frac{{\\sin \\pi \\,x}}{{5x}} = k\\)\\(\\Rightarrow{}\\) \\(\\mathop {{\\rm{lim}}}\\limits_{x \\to 0} \\left( {\\frac{{\\sin\\pi \\,x}}{{\\pi x}}} \\right)\\,.\\,\\frac{\\pi }{5} = k\\) \\(\\Rightarrow{}\\)\\((1)\\,.\\,\\frac{\\pi }{5} = k\\) \\(\\Rightarrow{}\\) \\(k = \\frac{\\pi }{5}\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x)\\, = \\frac{{2 - \\sqrt {x + 4} }}{{\\sin 2x}},\\,\\,(x \\ne 0),\\) iscontinuous function at \\(x = 0\\), then \\(f(0)\\) equals','(a)\\(\\frac{1}{4}\\)','(b)\\( - \\frac{1}{4}\\)','(c)\\(\\frac{1}{8}\\)','(d)\\( - \\frac{1}{8}\\)  ','4','FUNCTION, L, C, D','2','Answer =  (d)If \\(f(x)\\) is continuous at \\(x = 0,\\) then\\(f(0)\\, = \\,\\mathop {\\lim }\\limits_{x \\to 0} f(x)\\)\\( = \\mathop {\\lim}\\limits_{x \\to 0} \\frac{{2 - \\sqrt {x + 4} }}{{\\sin 2x}}\\)Using\\(\\left( {\\frac{0}{0}{\\rm{ }}\\,{\\rm{form}}} \\right),\\)L-Hospitals rule, \\(f(0) = \\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\left( { -\\frac{1}{{2\\sqrt {x + 4} }}} \\right)}}{{2\\cos 2x}} =  - \\frac{1}{8}\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If function \\(f(x) = \\left\\{ {\\begin{array}{*{20}{c}}{x\\,\\,\\,\\,\\,,}&{{\\rm{if}}\\,x\\,{\\rm{is rational}}}\\\\{1 - x,}&{{\\rm{if}}\\,x\\,{\\rm{is irrational}}}\\end{array},} \\right.\\) then \\(f(x)\\) is continuous at ...... number ofpoints','(a) \\(\\infty\\)','(b) 1','(c)0','(d)None of these   ','3','FUNCTION, L, C, D','2','Answer =  (c)At no point, function is continuous.    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ {\\begin{array}{*{20}{c}}{\\frac{{{x^2} - 9}}{{x - 3}}\\,,}&{{\\rm{if }}x \\ne 3}\\\\{2x + k\\,,}&{{\\rm{otherwise}}}\\end{array}} \\right.\\), is continuous at \\(x = 3,\\) then \\(k = \\)  ','(a)3','(b)0','(c)-6','(d)1/6  ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(\\mathop {\\lim }\\limits_{x \\to 3} f(x) = \\mathop {\\lim }\\limits_{x \\to 3}\\frac{{{x^2} - 9}}{{x - 3}} = \\mathop {\\lim }\\limits_{x \\to 3} (x + 3) = 6\\)and \\(f(3) = 2(3) + k = 6 + k\\)\\(f\\) is continuous at \\(x = 3\\); \\(\\therefore{}\\) \\(6 + k = 6 \\Rightarrow k =0\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function defined by \\(f(x)\\, = \\,\\left\\{ {\\begin{array}{*{20}{c}}{{{\\left( {{x^2} + {e^{\\frac{1}{{2 - x}}}}} \\right)}^{ - 1}}}&,&{x \\ne 2}\\\\k&,&{x = 2}\\end{array}} \\right.\\), is continuous from right at the point x = 2, then k is equal to','(a)0','(b)1/4','(c)-1/4','(d)None of these ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(f(x) = {\\left[ {{x^2} + {e^{\\frac{1}{{2 - x}}}}} \\right]^{ - 1}}\\) and\\(f(2) = k\\)If \\(f(x)\\) is continuous from right at \\(x = 2\\) then \\(\\mathop {\\lim}\\limits_{x \\to {2^ + }} f(x) = f(2) = k\\)\\(\\Rightarrow{}\\) \\(\\mathop {\\lim }\\limits_{x \\to {2^ + }} {\\left[ {{x^2} +{e^{\\frac{1}{{2 - x}}}}} \\right]^{ - 1}} = k\\)\\(\\Rightarrow{}\\) \\(k = \\mathop{\\lim }\\limits_{h \\to 0} f(2 + h)\\)\\(\\Rightarrow{}\\) \\(k = \\mathop {\\lim }\\limits_{h \\to 0} \\,{\\left[ {{{(2 +h)}^2} + {e^{\\frac{1}{{2 - (2 + h)}}}}} \\right]^{\\, - 1}}\\)\\(\\Rightarrow{}\\) \\(k = \\mathop {\\lim }\\limits_{h \\to 0} \\,{\\left[ {\\,4 + {h^2}+ 4h + {e^{ - 1/h}}\\,} \\right]^{\\, - 1}}\\)\\(\\Rightarrow{}\\) \\(k = {[4 + 0 + 0 + {e^{ - \\infty }}]^{\\, -1}}\\)\\(\\Rightarrow{}\\) \\(k = \\frac{1}{4}\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' For the function \\(f(x)\\, = \\frac{{{{\\log }_e}(1 + x) - {{\\log }_e}(1 -x)}}{x}\\) to be continuous at \\(x = 0,\\) the value of \\(f(0),\\) should be','(a)-1','(b)0','(c)-2','(d)2   ','4','FUNCTION, L, C, D','2','Answer =  (d)By L-Hospitals rule \\(\\mathop {\\lim }\\limits_{x \\to 0} f(x)\\) is 2.Therefore, for \\(f(x)\\) to be continuous, the value of function should be 2. ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x)\\, = \\,\\left\\{ {\\begin{array}{*{20}{c}}{\\frac{{\\sqrt {1 + kx}  - \\sqrt {1 - kx} }}{x}}&{{\\rm{,for}} - 1 \\le x < 0}\\\\{2{x^2} + 3x - 2}&{{\\rm{,}}\\,{\\rm{for }}\\,0 \\le \\,x \\le 1}\\end{array}} \\right.\\), is continuous at \\(x = 0\\),  then \\(k = \\)','(a)- 4','(b)- 3','(c)- 2','(d)- 1   ','3','FUNCTION, L, C, D','2','Answer =  (c)L.H.L. \\( = \\mathop {\\lim }\\limits_{x \\to {0^ - }} \\frac{{\\sqrt {1 + kx}  -\\sqrt {1 - kx} }}{x} = k\\)R.H.L. \\( = \\mathop {\\lim }\\limits_{x \\to {0^ + }} (2{x^2} + 3x - 2) =  - 2\\)Since it is continuous, L.H.L = R.H.L \\(\\Rightarrow{}\\) \\(k =  - 2\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(f(x) = \\frac{{1 - \\sin x + \\cos x}}{{1 + \\sin x + \\cos x}}\\) isnot defined at \\(x = \\pi .\\) The value of \\(f(\\pi ),\\) so that \\(f(x)\\) iscontinuous at \\(x = \\pi \\), is','(a)\\( - \\frac{1}{2}\\)','(b)\\(\\frac{1}{2}\\)','(c)- 1','(d)1    ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(f(x) = \\frac{{2{{\\cos }^2}\\frac{x}{2} - 2\\sin \\frac{x}{2}\\cos\\frac{x}{2}}}{{2{{\\cos }^2}\\frac{x}{2} + 2\\sin \\frac{x}{2}\\cos \\frac{x}{2}}} =\\frac{{\\cos \\frac{x}{2} - \\sin \\frac{x}{2}}}{{\\cos \\frac{x}{2} + \\sin\\frac{x}{2}}}\\)\\( = \\tan \\left( {\\frac{\\pi }{4} - \\frac{x}{2}} \\right)\\) at \\(x = \\pi \\),\\(f(\\pi ) =  - \\tan \\frac{\\pi }{4} =  - 1\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ \\begin{array}{l}\\frac{{1 - \\cos x}}{x},\\,x \\ne 0\\\\\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,k,\\,x = 0\\end{array} \\right.\\)is continuous at \\(x = 0\\) then \\(k = \\)    ','(a)0','(b)\\(\\frac{1}{2}\\)','(c)\\(\\frac{1}{4}\\)','(d)\\( - \\frac{1}{2}\\)  ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(f(x) = \\left\\{ \\begin{array}{l}\\frac{{1 - \\cos x}}{x},x \\ne 0\\\\\\,\\,\\,\\,\\,k\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,,x = 0\\end{array} \\right.\\) continuous at \\(x = 0\\)\\(\\mathop {\\lim }\\limits_{x \\to {0^ + }} f(x) = f(0)\\)\\(\\Rightarrow{}\\)\\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{2.{{\\sin }^2}x/2}}{x} = k\\)\\(\\Rightarrow{}\\) \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{2{{\\sin}^2}x/2}}{{{{\\left( {\\frac{x}{2}} \\right)}^2}}}.\\frac{x}{4} = k \\Rightarrow k =0\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' A function f on R into itself is continuous at a point a in R, iff for each \\(\\in  > 0\\), there exists, \\(\\delta  > 0\\)such that ','(a)\\(|f(x) - f','(a)| <  \\in \\)\\(\\Rightarrow{}\\)\\(|x - a| < \\delta \\)   (b)\\(|f(x) - f','(a)| >  \\in \\)\\(\\Rightarrow{}\\)\\(|x - a| > \\delta \\)(c)\\(|x - a| > \\delta \\)\\(\\Rightarrow{}\\)\\(|f(x) - f','(a)| >  \\in \\)(d)\\(|x - a| < \\delta \\)\\(\\Rightarrow{}\\)\\(|f(x) - f(a)| <  \\in \\) ','1','FUNCTION, L, C, D','2','Answer =  (a)It is obvious.  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' For the function \\(f(x) = \\left\\{ \\begin{array}{l}\\frac{{{e^{1/x}} - 1}}{{{e^{1/x}} + 1}},\\,\\,x \\ne 0\\\\0\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,,\\,\\,x = 0\\end{array} \\right.\\), which of the following is correct  ','(a)\\(\\mathop {\\lim }\\limits_{x \\to 0} f(x)\\)does not exist ','(b)\\(f(x)\\)is continuous at \\(x = 0\\)','(c)\\(\\mathop {\\lim }\\limits_{x \\to 0} f(x) = 1\\)','(d)\\(\\mathop {\\lim }\\limits_{x \\to 0} f(x)\\)exists but \\(f(x)\\)is not continuousat \\(x = 0\\)  ','4','FUNCTION, L, C, D','2','Answer =  (d)Given \\(f(x) = \\left\\{ \\begin{array}{l}\\frac{{{e^{\\frac{1}{x}}} - 1}}{{{e^{\\frac{1}{x}}} + 1}}\\,\\,,\\,\\,x \\ne 0\\\\0\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,,\\,x = 0\\end{array} \\right.\\)\\(\\Rightarrow{}\\) \\(\\mathop {\\lim }\\limits_{x \\to {0^ + }}\\frac{{{e^{\\frac{1}{x}}} - 1}}{{{e^{\\frac{1}{x}}} + 1}} = \\frac{{{e^\\infty } -1}}{{{e^\\infty } + 1}} =  - 1\\)\\(\\Rightarrow{}\\) \\(\\mathop {\\lim }\\limits_{x \\to {0^ - }}\\frac{{{e^{\\frac{1}{x}}} - 1}}{{{e^{\\frac{1}{x}}} + 1}} = \\frac{{1 - {e^{ -\\frac{1}{x}}}}}{{1 + {e^{\\frac{1}{x}}}}} = \\frac{{1 - {e^{ - \\infty }}}}{{1 +{e^\\infty }}} = 1\\)So, \\(\\mathop {\\lim }\\limits_{x \\to 0} f(x)\\) exists at \\(x = 0\\), but at \\(x =0\\) it is not continuous.  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function f is defined by \\(f(x) = 2x - 1,\\)if \\(x > 2\\), \\(f(x) =k\\;\\)if\\(x = 2\\)and \\({x^2} - 1,\\)if \\(x < 2\\)is continuous, then the value of kis equal to  ','(a)2','(b)3','(c)4','(d)-3   ','2','FUNCTION, L, C, D','2','Answer =  (b)We have \\(f(x) = 2x - 1,\\)if \\(x > 2,\\) \\(f(x) = k,\\)If \\(x = 2\\) and \\({x^2} - 1,\\) if \\(x < 2\\), function is continuous.\\(\\therefore{}\\) \\(\\mathop {\\lim }\\limits_{x \\to 2} f(x) =f(2)\\)\\(\\Rightarrow{}\\) \\(\\mathop {\\lim }\\limits_{x \\to 2} (2x - 1) = k\\Rightarrow k = 3\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' In the function \\(f(x) = \\frac{{2x - {{\\sin }^{ - 1}}x}}{{2x + {{\\tan }^{ -1}}x}},\\;(x \\ne 0)\\)is continuous at each point of its domain, then the value of\\(f(0)\\) is    ','(a)2','(b)\\(1/3\\)','(c)\\(2/3\\)','(d)\\( - 1/3\\) ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(f(x) = \\mathop {\\lim }\\limits_{x \\to 0} \\,\\left( {\\frac{{2x - {{\\sin }^{ -1}}x}}{{2x + {{\\tan }^{ - 1}}x}}} \\right) = f(0)\\),\\(\\left( {\\frac{0}{0}}\\right)\\)Applying L-Hospitals rule,\\(f(0) = \\mathop {\\lim }\\limits_{x \\to 0} \\frac{{\\left( {2 - \\frac{1}{{\\sqrt {1- {x^2}} }}} \\right)}}{{\\left( {2 + \\frac{1}{{1 + {x^2}}}} \\right)}}\\)\\(= \\frac{{2 - 1}}{{2 + 1}} = \\frac{1}{3}\\)Trick : f (0) =\\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{2 - \\frac{{{{\\sin }^{ -1}}x}}{x}}}{{2 + \\frac{{{{\\tan }^{ - 1}}x}}{x}}} = \\frac{{2 - 1}}{{2 + 1}} =\\frac{1}{3}\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(f(x) = |x| + \\frac{{|x|}}{x}\\) is    ','(a)Continuous at the origin  ','(b)Discontinuous at the origin because \\(\\vert{}\\)x\\(\\vert{}\\) is discontinuousthere','(c)Discontinuous at the origin because \\(\\frac{{|x|}}{x}\\) is discontinuousthere','(d)Discontinuous at the origin because both \\(\\vert{}\\)x\\(\\vert{}\\) and\\(\\frac{{|x|}}{x}\\) are discontinuous there   ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(|x|\\) is continuous at \\(x = 0\\) and \\(\\frac{{|x|}}{x}\\) is alsodiscontinuous at \\(x = 0\\)\\(\\therefore{}\\) \\(f(x) = \\,|x| + \\frac{{|x|}}{x}\\) is discontinuous at \\(x =0\\). ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The function \\(f(x) = \\frac{{2{x^2} + 7}}{{{x^3} + 3{x^2} - x - 3}}\\)isdiscontinuous for','(a)\\(x = 1\\) only','(b)\\(x = 1\\) and \\(x =  - 1\\) only','(c)\\(x = 1,x =  - 1,x =  - 3\\) only','(d)\\(x = 1,x =  - 1,x =  - 3\\) and other values of x    ','3','FUNCTION, L, C, D','2','Answer =  (c)\\(f(x) = \\frac{{2{x^2} + 7}}{{{x^2}(x + 3) - 1(x + 3)}}\\frac{{9{x^2} +7}}{{({x^2} - 1)(x + 3)}}\\)\\(= \\frac{{2{x^2} + 7}}{{(x - 1)(x + 1)(x + 3)}}\\)Hence points of discontinuity are\\(x = 1\\), \\(x =  - 1\\) and \\(x =  - 3\\) only. ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Let \\(f(x) = \\left\\{ \\begin{array}{l}{x^p}\\sin \\frac{1}{x},x \\ne 0\\\\0\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,,x = 0\\end{array} \\right.\\) then \\(f(x)\\)is continuous but not differential at \\(x =0\\) if         ','(a)\\(0 < p \\le 1\\)','(b)\\(1 \\le p < \\infty \\)','(c)\\( - \\infty  < p < 0\\)','(d)p = 0    ','1','FUNCTION, L, C, D','2','Answer =  (a)\\(f(x) = {x^p}\\sin \\frac{1}{x},x \\ne 0\\) and \\(f(x) = 0,\\;x = 0\\)Since at \\(x = 0\\),\\(f(x)\\) is a continuous function\\(\\therefore{}\\) \\(\\mathop {\\lim }\\limits_{x \\to 0} f(x) = f(0) = 0\\)\\(\\Rightarrow{}\\) \\(\\mathop {\\lim }\\limits_{x \\to 0} \\,{x^p}\\sin \\frac{1}{x} = 0\\Rightarrow p > 0\\).\\(f(x)\\) is differentiable at \\(x = 0\\), if \\(\\mathop {\\lim }\\limits_{x \\to 0}\\frac{{f(x) - f(0)}}{{x - 0}}\\) exists\\(\\Rightarrow{}\\) \\(\\mathop {\\lim }\\limits_{x \\to 0} \\frac{{{x^p}\\sin\\frac{1}{x} - 0}}{{x - 0}}\\) exists\\(\\Rightarrow{}\\) \\(\\mathop {\\lim }\\limits_{x \\to 0} {x^{p - 1}}\\sin\\frac{1}{x}\\) exists\\(\\Rightarrow{}\\) \\(p - 1 > 0\\) or \\(p > 1\\)If \\(p \\le 1\\), then \\(\\mathop {\\lim }\\limits_{x \\to 0} {x^{p - 1}}\\sin \\left({\\frac{1}{x}} \\right)\\) does not exist and at \\(x = 0\\) \\(f(x)\\) is notdifferentiable.\\(\\therefore{}\\) for  \\(0 < p \\le 1\\) f(x) is a continuous function at \\(x = 0\\)but not differentiable.   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\)\\(\\left\\{ \\begin{array}{l}\\frac{{1 - (x)}}{{1 + x}},\\,\\,\\,\\,\\,x \\ne  - 1\\\\\\,\\,1\\,\\,\\,\\,\\,\\,\\,\\,\\,,\\,\\,\\,\\,\\,x =  - 1\\end{array} \\right.\\), then  the value of \\(f(|2k|)\\)will be (where ','(a)Continuous at \\(x =  - 1\\)','(b)Continuous at \\(x = 0\\)','(c)Discontinuous at \\(x = \\frac{1}{2}\\)','(d)All of these ','4','FUNCTION, L, C, D','2','Answer =  (d)\\(f(x) = \\left\\{ \\begin{array}{l}\\frac{{1 - |x|}}{{1 + x}},\\,x \\ne  - 1\\\\1\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,,\\,\\,x =  - 1\\end{array} \\right.\\) and \\(f(x) = \\left\\{ \\begin{array}{l}1\\,\\,\\,\\,\\,\\,\\,\\,\\,,x < 0\\\\\\frac{{1 - x}}{{1 + x}}\\,,x \\ge 0\\end{array} \\right.\\)\\(f(2x) = \\left\\{ \\begin{array}{l}1\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,,\\,\\,x < 0\\\\\\frac{{1 - [2x]}}{{1 + [2x]}},\\,\\,x > 0\\end{array} \\right.\\)\\(\\Rightarrow{}\\) \\(f(2x) = \\left\\{ \\begin{array}{l}1\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,,\\,\\,x < 0\\\\1\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,,\\,\\,0 \\le x < \\frac{1}{2}\\\\0\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,,\\,\\,\\frac{1}{2} \\le x \\le 1\\\\- \\frac{1}{3}\\,\\,\\,\\,\\,,\\,\\,\\,1 \\le x < \\frac{3}{2}\\end{array} \\right.\\)\\(\\Rightarrow{}\\) \\(f(x)\\), for all values of x where \\(x < \\frac{1}{2}\\)acontinous function and for \\(x = \\frac{1}{2}\\) and \\(x = 1\\) \\(f(x)\\)be adiscontinous  function.   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Function \\(f(x) = \\frac{{1 - \\cos 4x}}{{8{x^2}}},\\) where \\(x \\ne 0\\)and \\(f(x)= k\\) where \\(x = 0\\) is a continous function at \\(x = 0\\)then the value of kwill be','(a)\\(k = 0\\)','(b)\\(k = 1\\)','(c)\\(k =  - 1\\)','(d)None of these  ','2','FUNCTION, L, C, D','2','Answer =  (b)\\(f(x) = \\left\\{ \\begin{array}{l}\\frac{{1 - \\cos 4x}}{{8{x^2}}},x \\ne 0\\\\k\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,,\\,x = 0\\end{array} \\right.\\)If \\(f(x)\\) is continous function at point \\(x = 0\\) then\\(\\mathop {\\lim }\\limits_{x \\to 0 + } [f(x) = \\mathop {\\lim }\\limits_{x \\to 0 -} [f(x)]\\)\\(\\mathop {\\lim }\\limits_{x \\to 0} [f(x)] = \\mathop {\\lim }\\limits_{h \\to 0 - }[f(0 + h)]\\)\\(= \\mathop {\\lim }\\limits_{h \\to 0} [f(h)] = \\mathop {\\lim }\\limits_{h \\to 0}\\frac{{1 - \\cos 4h}}{{8{h^2}}} = \\mathop {\\lim }\\limits_{h \\to 0} \\frac{{2{{\\sin}^2}2h}}{{8{h^2}}} = \\mathop {\\lim }\\limits_{h \\to 0} \\frac{{{{\\sin}^2}2h}}{{4{h^2}}}\\)\\(= \\mathop {\\lim }\\limits_{h \\to 0} {\\left( {\\frac{{\\sin 2h}}{{2h}}} \\right)^2}= {(1)^2} = 1\\)\\(\\mathop {\\lim }\\limits_{x \\to {0^ - }} f(x)\\mathop { = \\lim}\\limits_{\\,\\,\\,\\,\\,h \\to 0} [f(0 - h)\\)\\(= \\mathop {\\lim }\\limits_{h \\to 0} [f( - h)] = \\mathop {\\lim }\\limits_{h \\to 0}\\frac{{1 - \\cos 4( - h)}}{{8{{( - h)}^2}}}\\)\\( = \\mathop {\\lim }\\limits_{h \\to 0} \\frac{{1 - \\cos4h}}{{8{h^2}}}\\)\\(= 1\\)\\(f(0) = 1 \\Rightarrow k = 1.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = \\left\\{ \\begin{array}{l}\\,\\,\\,\\,\\,\\,\\,{e^x};\\,\\,\\,\\,x \\le 0\\\\|1 - x|;\\,\\,x > 0\\end{array} \\right.\\), then','(a)\\(f(x)\\) is differentiable at \\(x = 0\\)','(b)\\(f(x)\\) is continuous at \\(x = 0\\)','(c)\\(f(x)\\) is differentiable at \\(x = 1\\)','(d)\\(f(x)\\) is continuous at \\(x = 1\\)   ','(b,','FUNCTION, L, C, D','2','Answer =  (b, d) \\(f(x) = \\left\\{ \\begin{array}{l}{e^x}\\,\\,;\\,\\,\\,x \\le 0\\\\1 - x;\\,\\,0 < x \\le 1\\\\x - 1\\,;\\,\\,x > 1\\end{array} \\right.\\)\\(Rf(0) = \\mathop {\\lim }\\limits_{h \\to 0} \\frac{{f(0 + h) - f(0)}}{h} = \\mathop{\\lim }\\limits_{h \\to 0} \\frac{{1 - h - 1}}{h} =  - 1\\)\\(Lf(0) = \\mathop {\\lim }\\limits_{h \\to 0} \\frac{{f(0 - h) - f(0)}}{{ - h}} =\\mathop {\\lim }\\limits_{h \\to 0} \\frac{{{e^{ - h}} - 1}}{{ - h}} = 1\\)So, it is not differentiable at \\(x = 0\\).Similarly, it is not differentiable at \\(x = 1\\).But it is continous at \\(x = 0\\), 1. ',CURRENT_TIMESTAMP);
