INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f:R \\to R\\) satisfies \\(f(x + y) = f(x) + f(y)\\), for all \\(x,\\;y \\in R\\)and \\(f(1) = 7\\), then \\(\\sum\\limits_{r = 1}^n {f(r)} \\) is ','(a)\\(\\frac{{7n}}{2}\\)','(b)\\(\\frac{{7(n + 1)}}{2}\\)','(c)\\(7n(n + 1)\\)','(d)\\(\\frac{{7n(n + 1)}}{2}\\)   1','4','FUNCTION, L, C, D','2','Answer =  (d)\\(f(x + y) = f(x) + f(y)\\)Put \\(x = 1,\\,y = 0\\)\\(\\Rightarrow{}\\) \\(f(1) = f(1) + f(0) = 7\\)Put \\(x = 1,\\,y = 1\\) \\(\\Rightarrow{}\\) \\(f(2) = 2.f(1) = 2.7\\)Similarly \\(f(3) = 3.7\\) and so on\\(\\therefore \\sum\\limits_{r = 1}^n {f(r) = 7\\,(1 + 2 + 3 + ..... + n)} \\) =\\(\\frac{{7n(n + 1)}}{2}\\). 1',CURRENT_TIMESTAMP);
