INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f(x) = {\\mathop{\\rm sgn}} ({x^3})\\), then ','(a)f is continuous but not derivable at \\(x = 0\\)','(b)\\(f({0^ + }) = 2\\)','(c)\\(f({0^ - }) = 1\\)','(d)f is not derivable at \\(x = 0\\)   1','4','FUNCTION, L, C, D','2','Answer =  (d)Here, \\(f(x) = {\\mathop{\\rm sgn}} {x^3} = \\left\\{ \\begin{array}{l}\\left\\{ {\\begin{array}{*{20}{c}}{\\frac{{{x^3}}}{{|{x^3}|}},}&{{\\rm{for}}}&{{x^3} \\ne 0}\\\\{0{\\rm{    }},}&{{\\rm{for}}}&{{x^3} = 0}\\end{array}} \\right.\\\\\\left\\{ {\\begin{array}{*{20}{c}}{\\frac{x}{{|x|}},}&{{\\rm{for}}}&{x \\ne 0}\\\\{0{\\rm{   ,}}}&{{\\rm{for}}}&{x = 0}\\end{array}} \\right.\\\\\\left\\{ {\\begin{array}{*{20}{c}}{ - 1,}&{x < 0}\\\\{0,}&{x = 0}\\\\{1,}&{x > 0}\\end{array}} \\right.\\end{array} \\right.\\)Thus, \\(f(x) = {\\mathop{\\rm sgn}} {x^3} = {\\mathop{\\rm sgn}} x,\\) which isneither continuous nor derivable at 0. Note that\\(f({0^ + }) = \\mathop {{\\rm{lim}}}\\limits_{h \\to {0^ + }} \\,\\frac{{f(0 + h) -f(0)}}{h}\\)\\(= \\mathop {{\\rm{lim}}}\\limits_{h \\to {0^ + }} \\,\\frac{{1 - 0}}{h} \\to \\infty\\)and \\(f({0^ - }) = \\mathop {{\\rm{lim}}}\\limits_{h \\to {0^ - }} \\,\\frac{{f(0 -h) - f(0)}}{h}\\)\\( = \\mathop {{\\rm{lim}}}\\limits_{h \\to {0^ - }} \\,\\frac{{ - 1 -0}}{h} \\to \\infty \\).\\(\\therefore{}\\) \\(f({0^ + }) \\ne f({0^ - })\\),  \\(\\therefore{}\\) f is notderivable at \\(x = 0\\). 1',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If \\(f:R \\to R\\) and \\(g:R \\to R\\) are given by \\(f(x) = \\;|x|\\) and \\(g(x) =\\;|x|\\) for each \\(x \\in R\\), then \\(\\{ x \\in R\\;:g(f(x)) \\le f(g(x))\\}  =\\)','(a)\\(Z \\cup ( - \\infty ,\\;0)\\)','(b)\\(( - \\infty ,0)\\)','(c)Z','(d)R 1','4','FUNCTION, L, C, D','2','Answer =  (d)\\(g(f(x)) \\le f(g(x))\\) \\(\\Rightarrow{}\\) \\(g(|x|) \\le f[x]\\)\\(\\Rightarrow{}\\) \\([|x|]\\, = \\,|[x]|\\).This is true for \\(x \\in R.\\). 1',CURRENT_TIMESTAMP);
