INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the centroid of triangle whose vertices are (a,1, 3), (- 2, b, -5) and (4,7, c) be the origin, then the values of a, b, c are','(a)- 2, -8, -2','(b)2, 8, -2','(c)-2, -8, 2','(d)7, -1, 0   ','3','3D','2','Answer =  (c)\\(0 = \\frac{{a - 2 + 4}}{3}\\,\\, \\Rightarrow \\,\\,a =  - 2,\\,\\,0 = \\frac{{1 + b+ 7}}{3}\\, \\Rightarrow \\,\\,b =  - \\,8\\)and \\(0 = \\frac{{3 - 5 + c}}{3}\\,\\, \\Rightarrow \\,\\,c = 2\\).   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Which of the following set of points are non- collinear','(a)(1, -1, 1), (-1, 1, 1), (0, 0, 1)','(b)(1, 2, 3), (3, 2, 1), (2, 2, 2)','(c)(-2,4, -3), (4, -3, -2), (-3, -2, 4)','(d)(2, 0, -1), (3, 2, -2), (5, 6, -4)  ','3','3D','2','Answer =  (c)Check option (c), \\(\\frac{{4 - ( - 2)}}{{ - 3 - 4}} \\ne \\frac{{ - 3 - 4}}{{ -2 - ( - 3)}}\\)Therefore, this set of points is non-collinear.    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If a straight line in space is equally inclined to the co-ordinate axes, thecosine of its angle of inclination to any one of the axes is  ','(a)\\(\\frac{1}{3}\\)','(b)\\(\\frac{1}{2}\\)','(c)\\(\\frac{1}{{\\sqrt 3 }}\\)','(d)\\(\\frac{1}{{\\sqrt 2 }}\\) ','3','3D','2','Answer =  (c) Here, \\(\\cos \\alpha  = \\cos \\beta  = \\cos \\gamma \\)   \\(\\therefore \\)\\(3\\,{\\cos ^2}\\alpha  = 1\\,\\, \\Rightarrow \\,\\,\\alpha  = {\\cos ^{ - 1}}\\left( { \\pm\\frac{1}{{\\sqrt 3 }}} \\right).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If a line makes angles of \\({30^o}\\)and \\({45^o}\\)with x-axis and        y-axis,then the angle made by it with \\(z - \\)axis is','(a)\\({45^o}\\)','(b)\\({60^o}\\)','(c)\\({120^o}\\)','(d)None of these ','4','3D','2','Answer =  (d)\\(\\cos \\gamma  = \\sqrt {1 - \\frac{3}{4} - \\frac{1}{2}}  = \\sqrt {\\frac{{ -1}}{4},} \\)which is not possible.   ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Direction ratios of the normal to the plane passing through the points (0, 1,1), (1, 1, 2)and (-1, 2, - 2) are','(a)(1, 1, 1)','(b)(2, 1, -1)','(c)(1, 2, -1)','(d)(1, - 2,  - 1)  ','4','3D','2','Answer =  (d) \\(l\\,(1) + m(0) + n(2 - 1) = 0\\,\\, \\Rightarrow \\,\\,l + n = 0\\)and\\(l\\,( - 2) + m(1) + n( - 4) = 0\\,\\,\\,\\, \\Rightarrow \\,\\,\\,2l - m + 4n = 0\\)\\(\\therefore \\,\\,\\,l =  - \\frac{m}{2} =  - \\,n.\\)Hence direction ratios are \\((1,\\,\\, - 2,\\,\\, - 1)\\).  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the length of a vector be 21 and direction ratios be 2, - 3, 6 then itsdirection cosines are','(a)\\(\\frac{2}{{21}},\\frac{{ - 1}}{7},\\frac{2}{7}\\)','(b)\\(\\frac{2}{7},\\frac{{ -3}}{7},\\frac{6}{7}\\)','(c)\\(\\frac{2}{7},\\frac{3}{7},\\frac{6}{7}\\)','(d)None of these   ','2','3D','2','Answer =  (b)D.c.s are \\(\\frac{2}{{\\sqrt {{2^2} + {{( - 3)}^2} + {6^2}} }},\\,\\,\\frac{{ -3}}{{\\sqrt {49} }}{\\rm{and}}\\,\\frac{6}{{\\sqrt {49} }}\\)or \\(\\frac{2}{7},\\,\\frac{{ - 3}}{7},\\,\\,\\frac{6}{7}\\).   ',CURRENT_TIMESTAMP);
