INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the co-ordinates of  the points \\(A,B,C,D\\) be (2, 3, -1),        (3, 5, -3), (1, 2, 3) and (3, 5, 7) respectively, then the projection of \\(AB\\) on \\(CD\\)is','(a)0','(b)1','(c)2','(d)\\(\\sqrt 3 \\)   ','1','3D','2','Answer =  (a)Since AB and CD are perpendicular, therefore projection of AB on CD is 0.  ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If the co-ordinates of the points  P and Q be (1, -2, 1) and (2, 3, 4) and O bethe origin, then','(a)\\(OP = OQ\\)','(b)\\(OP\\, \\bot \\,OQ\\)','(c)\\(OP||OQ\\)','(d)None of these   ','2','3D','2','Answer =  (b) \\({a_1}{a_2} + {b_1}{b_2} + {c_1}{c_2} = 0,\\)so \\(OP \\bot \\,OQ\\).   ',CURRENT_TIMESTAMP);
