INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The angle between the straight lines \\(\\frac{{x + 1}}{2} = \\frac{{y - 2}}{5} =\\frac{{z + 3}}{4}\\) and  \\(\\frac{{x - 1}}{1} = \\frac{{y + 2}}{2} = \\frac{{z -3}}{{ - 3}}\\) is','(a)\\(45^\\circ \\)','(b)\\(30^\\circ \\)','(c)\\(60^\\circ \\)','(d)\\(90^\\circ \\)  ','4','3D','2','Answer =  (d)\\(\\theta  = {\\cos ^{ - 1}}\\frac{{(2 + 10 - 12)}}{{\\sqrt {4 + 25 + 16} \\sqrt{1 + 4 + 9} }}\\, = {\\cos ^{ - 1}}(0)\\)\\(\\Rightarrow{}\\)\\(\\theta  = {90^o}\\). ',CURRENT_TIMESTAMP);
