INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' If (4, 0) and (-4, 0) be the vertices and (6, 0) and (-6, 0) be the foci of ahyperbola, then its eccentricity is','(a)5/2','(b)2','(c)3/2','(d)\\(\\sqrt 2 \\) ','3','CONIC SECTIONS','2','Answer =  (c)Vertices \\(( \\pm 4,\\,0) \\equiv ( \\pm a,\\,0)\\) \\(\\Rightarrow{}\\) \\(a = 4\\)Foci \\(( \\pm 6,\\,0) \\equiv ( \\pm ae,\\,0)\\) \\(\\Rightarrow{}\\) \\(e = \\frac{6}{4} =\\frac{3}{2}\\).  ',CURRENT_TIMESTAMP);
