INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The equation of the parabola with its vertex at the origin, axis on the y-axisand passing through the point (6, -3) is','(a)\\({y^2} = 12x + 6\\)','(b)\\({x^2} = 12y\\)','(c)\\({x^2} =  - 12y\\)','(d)\\({y^2} =  - 12x + 6\\)   ','3','CONIC SECTIONS','2','Answer =  (c)Since the axis of parabola is  y-axis\\(\\therefore{}\\) Equation of parabola \\({x^2} = 4ay\\)Since it passes through (6, - 3)\\(\\therefore{}\\) 36 \\( =  - \\,12a\\,\\,\\, \\Rightarrow \\,a =  - 3\\)\\(\\therefore{}\\) Equation of parabola is \\({x^2} =  - 12y\\).    ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' Focus and directrix of the parabola \\({x^2} =  - 8ay\\) are','(a)\\((0,\\; - 2a)\\;{\\rm{and}}\\;y = 2a\\) ','(b)\\((0,\\;2a)\\;{\\rm{and}}\\;y =  - 2a\\)','(c)\\((2a,\\;0)\\;{\\rm{and}}\\;x =  - 2a\\)','(d)\\(( - 2a,\\;0)\\;{\\rm{and}}\\;x = 2a\\)  ','1','CONIC SECTIONS','2','Answer =  (a)Given equation is \\({x^2} =  - 8\\,ay.\\) Here \\(A = 2a\\)Focus of parabola \\((0,\\, - A)\\) i.e. (0, - 2a)Directrix y = A i.e., y = 2a.    ',CURRENT_TIMESTAMP);
