INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files',' The angle of intersection between the curves \\({x^2} = 4(y + 1)\\) and \\({x^2} = - 4(y + 1)\\) is ','(a)\\(\\frac{\\pi }{6}\\)','(b)\\(\\frac{\\pi }{4}\\)','(c)0','(d)\\(\\frac{\\pi }{2}\\)    ','3','CONIC SECTIONS','2','Answer =  (c)Point of intersection \\( = (0,\\, - 1)\\); \\(\\frac{{dy}}{{dx}} =\\frac{{2x}}{4}\\) and \\(\\frac{{ - 2x}}{4}\\)\\(\\therefore{}\\) \\({m_1} = 0,\\,\\,{m_2} = 0\\) \\(\\Rightarrow{}\\) \\(\\theta  ={0^o}.\\)  ',CURRENT_TIMESTAMP);
